CREATE OR REPLACE PROCEDURE vipe_obter_inf_item_composto ( cd_material_p bigint, cd_unidade_medida_dose_p text, cd_unid_med_cons_p INOUT text, qt_conversao_p INOUT bigint) AS $body$
DECLARE


cd_unidade_medida_consumo_w	varchar(30);
qt_conversao_w			double precision	:= 1;


BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then
	begin

	select	cd_unidade_medida_consumo
	into STRICT	cd_unidade_medida_consumo_w
	from	material
	where	cd_material = cd_material_p;

	select	coalesce(max(qt_conversao),0)
	into STRICT	qt_conversao_w
        	from	material_conversao_unidade
	where	cd_material = cd_material_p
	and	cd_unidade_medida = cd_unidade_medida_dose_p;

	end;
end if;

cd_unid_med_cons_p	:= cd_unidade_medida_consumo_w;
qt_conversao_p		:= qt_conversao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
