CREATE OR REPLACE PROCEDURE vipe_obter_item_composto ( nr_prescricao_p bigint, qt_item_p bigint, nr_prescricoes_p text, cd_item_p text, ds_compostos_p INOUT text) AS $body$
DECLARE


nr_prescr_w		bigint;
nr_seq_horario_w	bigint;
ds_compostos_w		varchar(255);


BEGIN

if (nr_prescricao_p > 0) then
	nr_prescr_w	:= nr_prescricao_p;
else
	nr_prescr_w	:= Obter_Max_NrPrescricao(nr_prescricoes_p);
end if;

if (nr_prescr_w > 0) then
	begin

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_horario_w
	from	prescr_mat_hor
	where	cd_material	= cd_item_p
	and	nr_prescricao	= nr_prescr_w;
	--and	qt_dose		= qt_item_p;
	if (nr_seq_horario_w > 0) then
		ds_compostos_w	:= substr(VIPE_obter_compostos(nr_seq_horario_w),1,255);
	end if;

	end;
end if;

ds_compostos_p	:= ds_compostos_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
