SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE vipe_verifica_dose_maxima ( nr_prescricao_p bigint, nr_sequencia_p bigint, ie_utiliza_horarios_p text, qt_dose_p bigint, ds_erro_p INOUT text, ds_erro2_p INOUT text) AS $body$
DECLARE

 
qt_max_prescricao_w		double precision;
cd_unidade_medida_consumo_w	varchar(30);
cd_unid_med_limite_w		varchar(30);
qt_limite_pessoa_w		double precision;
qt_conversao_dose_w		double precision;
qt_conversao_dose_limite_w	double precision;
qt_dose_w			double precision;
qt_dose_ww			double precision;
qt_dose_limite_w		double precision;
cd_unidade_medida_dose_w	varchar(30);
cd_material_w			integer;
ie_dose_limite_w		varchar(15);
nr_ocorrencia_w			double precision;
ie_via_aplicacao_w		varchar(5);
ie_justificativa_w		varchar(5);
ds_justificativa_w		varchar(2000);
ds_dose_limite_w		varchar(2000);
cd_prescritor_w			varchar(50);
cd_setor_atendimento_w		integer;
qt_regra_w			bigint;
qt_idade_w			bigint;
qt_peso_w			real;
qt_limite_peso_w		double precision;
nr_horas_validade_w		integer;
ie_agrupador_w			prescr_material.ie_agrupador%type;
ie_objetivo_w				prescr_material.ie_objetivo%type;

c01 CURSOR FOR 
	SELECT	coalesce(qt_limite_pessoa,0), 
		coalesce(ie_dose_limite,'DOSE'), 
		cd_unid_med_limite, 
		coalesce(ie_justificativa,'S') 
	from	material_prescr 
	where	cd_material							= cd_material_w 
	and	coalesce(cd_setor_atendimento, coalesce(cd_setor_atendimento_w,0))	= coalesce(cd_setor_atendimento_w,0) 
	and	coalesce(ie_via_aplicacao, coalesce(ie_via_aplicacao_w,0))		= coalesce(ie_via_aplicacao_w,0) 
	and	(qt_limite_pessoa IS NOT NULL AND qt_limite_pessoa::text <> '') 
	and	qt_idade_w between coalesce(qt_idade_min,0) and coalesce(qt_idade_max,999) 
	and	qt_peso_w between coalesce(qt_peso_min,0) and coalesce(qt_peso_max,999) 
	and	ie_tipo								= '2' 
	and	((coalesce(cd_especialidade::text, '') = '') or (obter_se_especialidade_medico(cd_prescritor_w, cd_especialidade) = 'S')) 
	and	(((coalesce(ie_tipo_item,'TOD') = 'SOL') and (ie_agrupador_w = 4)) or 
		 ((coalesce(ie_tipo_item,'TOD') = 'OUT') and (ie_agrupador_w <> 4)) or (coalesce(ie_tipo_item,'TOD') = 'TOD')) 
	and   coalesce(ie_objetivo, coalesce(ie_objetivo_w, 'N')) = coalesce(ie_objetivo_w,'N')	 
	order by nr_sequencia;


BEGIN 
 
ds_erro_p	:= '';
ds_erro2_p	:= '';
 
select	cd_material, 
	cd_unidade_medida_dose, 
	coalesce(qt_dose_p, qt_dose), 
	nr_ocorrencia, 
	ie_via_aplicacao, 
	ds_justificativa, 
	ie_agrupador, 
	coalesce(ie_objetivo,'N') 
into STRICT	cd_material_w, 
	cd_unidade_medida_dose_w, 
	qt_dose_ww, 
	nr_ocorrencia_w, 
	ie_via_aplicacao_w, 
	ds_justificativa_w, 
	ie_agrupador_w, 
	ie_objetivo_w 
from	prescr_material 
where	nr_prescricao	= nr_prescricao_p 
and	nr_sequencia	= nr_sequencia_p;
 
select	max(cd_prescritor), 
	max(nr_horas_validade) 
into STRICT	cd_prescritor_w, 
	nr_horas_validade_w 
from	prescr_medica 
where	nr_prescricao	= nr_prescricao_p;
 
if (coalesce(cd_material_w,0) > 0) and (cd_unidade_medida_dose_w IS NOT NULL AND cd_unidade_medida_dose_w::text <> '') and (coalesce(qt_dose_ww,0) > 0) then 
	begin 
 
	Select	count(*) 
	into STRICT	qt_regra_w 
	from	material 
	where	cd_material	= cd_material_w 
	and	(qt_limite_pessoa IS NOT NULL AND qt_limite_pessoa::text <> '');
 
	if (qt_regra_w > 0) then 
		begin 
		Select	coalesce(qt_max_prescricao,0), 
			cd_unidade_medida_consumo, 
			cd_unid_med_limite, 
			coalesce(qt_limite_pessoa,0), 
			coalesce(ie_dose_limite,'DOSE') 
		into STRICT	qt_max_prescricao_w, 
			cd_unidade_medida_consumo_w, 
			cd_unid_med_limite_w, 
			qt_limite_pessoa_w, 
			ie_dose_limite_w 
		from	material 
		where	cd_material	= cd_material_w;
		 
		/* Pega o setor de atendimento da prescrição e a idade do paciente */
 
		select	cd_setor_atendimento, 
			(obter_idade_pf(cd_pessoa_fisica, clock_timestamp(), 'A'))::numeric , 
			coalesce(qt_peso,0) 
		into STRICT	cd_setor_atendimento_w, 
			qt_idade_w, 
			qt_peso_w 
		from	prescr_medica 
		where	nr_prescricao	= nr_prescricao_p;
	 
		if (cd_unidade_medida_consumo_w = cd_unidade_medida_dose_w) then 
			qt_conversao_dose_w	:= 1;
		else 
			begin 
			select	coalesce(max(qt_conversao),1) 
			into STRICT	qt_conversao_dose_w 
			from	material_conversao_unidade 
			where	cd_material		= cd_material_w 
			and	cd_unidade_medida	= cd_unidade_medida_dose_w;
			exception 
				when others then 
				qt_conversao_dose_w	:= 1;
			end;
		end if;
 
		qt_dose_w	:= (trunc(qt_dose_ww * 1000 / qt_conversao_dose_w)/ 1000);
	 
		if (qt_max_prescricao_w > 0) and (qt_max_prescricao_w < coalesce(qt_dose_w,0)) then 
			ds_erro_p	:= wheb_mensagem_pck.get_texto(277286, 'QT_MAX_PRESCRICAO_P=' || qt_max_prescricao_w || ';CD_UNIDADE_MEDIDA_CONSUMO_P=' || cd_unidade_medida_consumo_w);
		end if;
 
		if (cd_unidade_medida_consumo_w = cd_unid_med_limite_w) then 
			qt_conversao_dose_limite_w	:= 1;
		else 
			begin 
			select	coalesce(max(qt_conversao),1) 
			into STRICT	qt_conversao_dose_limite_w 
			from	material_conversao_unidade 
			where	cd_material		= cd_material_w 
			and	cd_unidade_medida	= cd_unid_med_limite_w;
			exception 
				when others then 
					qt_conversao_dose_limite_w	:= 1;
			end;
		end if;
 
		qt_dose_w		:= (trunc(dividir(qt_dose_ww * 1000, qt_conversao_dose_w))/ 1000);
		qt_dose_limite_w	:= (trunc(dividir(qt_limite_pessoa_w * 1000, qt_conversao_dose_limite_w))/ 1000);
	 
		if (ie_dose_limite_w = 'DIA') then 
			begin 
			if (nr_horas_validade_w	> 24) then 
				nr_ocorrencia_w	:= trunc(((nr_ocorrencia_w * 24) / nr_horas_validade_w));
			end if;
			qt_dose_w	:= qt_dose_w * coalesce(nr_ocorrencia_w,1);
			qt_dose_w	:= qt_dose_w + obter_dose_medic_dia(nr_prescricao_p,nr_sequencia_p,ie_utiliza_horarios_p,cd_material_w);
			end;
		elsif (ie_dose_limite_w = 'AT') then 
			begin			 
			qt_dose_w	:= qt_dose_w * coalesce(nr_ocorrencia_w,1);
			qt_dose_w	:= qt_dose_w + obter_dose_medic_atend_dia(nr_prescricao_p,nr_sequencia_p,qt_dose_w,ie_utiliza_horarios_p,cd_material_w);
			end;
		elsif (qt_peso_w > 0) and (ie_dose_limite_w = 'KG/DIA') then 
			begin			 
			qt_dose_w	:= qt_dose_w / coalesce(qt_peso_w,1);/*por KG*/
 
			 
			/*por dia*/
			if (nr_horas_validade_w	> 24) then 
				nr_ocorrencia_w	:= trunc(((nr_ocorrencia_w * 24) / nr_horas_validade_w));
			end if;
			qt_dose_w	:= qt_dose_w * coalesce(nr_ocorrencia_w,1);
			qt_dose_w	:= qt_dose_w + obter_dose_medic_dia(nr_prescricao_p,nr_sequencia_p,ie_utiliza_horarios_p,cd_material_w);			
			end;			
		elsif (qt_peso_w > 0) and (ie_dose_limite_w = 'KG/D') then 
			begin			 
			qt_dose_w	:= qt_dose_w / coalesce(qt_peso_w,1);/*por KG*/
 
			 
			/*por dose*/
 
			/*if	(nr_horas_validade_w	> 24) then 
				nr_ocorrencia_w	:= trunc(((nr_ocorrencia_w * 24) / nr_horas_validade_w)); 
			end if;*/
			qt_dose_w	:= qt_dose_w * coalesce(nr_ocorrencia_w,1);
			qt_dose_w	:= qt_dose_w + obter_dose_medic_dia(nr_prescricao_p,nr_sequencia_p,ie_utiliza_horarios_p,cd_material_w);			
			end;
		end if;
		 
		if (qt_dose_limite_w > 0) and (qt_dose_limite_w < coalesce(qt_dose_w,0)) then 
			ds_dose_limite_w	:= obter_valor_dominio(1851, ie_dose_limite_w);
			ds_erro2_p 		:= wheb_mensagem_pck.get_texto(277256, 'QT_LIMITE_PESSOA_P=' || qt_limite_pessoa_w || ';CD_UNID_MED_LIMITE_P=' || cd_unid_med_limite_w || ';DS_DOSE_LIMITE_P=' || ds_dose_limite_w);
		end if;
		end;
	else	 
		begin 
 
		/* Pega o setor de atendimento da prescrição e a idade do paciente */
 
		select	cd_setor_atendimento, 
			(obter_idade_pf(cd_pessoa_fisica, clock_timestamp(), 'A'))::numeric , 
			coalesce(qt_peso,0) 
		into STRICT	cd_setor_atendimento_w, 
			qt_idade_w, 
			qt_peso_w 
		from	prescr_medica 
		where	nr_prescricao	= nr_prescricao_p;
 
		/* Verifica se tem alguma regra para os dados informados */
 
		select	count(*) 
		into STRICT	qt_regra_w 
		from	material_prescr 
		where	cd_material							= cd_material_w 
		and	coalesce(cd_setor_atendimento, coalesce(cd_setor_atendimento_w,0))	= coalesce(cd_setor_atendimento_w,0) 
		and	coalesce(ie_via_aplicacao, coalesce(ie_via_aplicacao_w,0))		= coalesce(ie_via_aplicacao_w,0) 
		and	qt_idade_w between coalesce(qt_idade_min,0) and coalesce(qt_idade_max,999) 
		and	qt_peso_w between coalesce(qt_peso_min,0) and coalesce(qt_peso_max,999) 
		and	(qt_limite_pessoa IS NOT NULL AND qt_limite_pessoa::text <> '') 
		and	((coalesce(cd_especialidade::text, '') = '') or (obter_se_especialidade_medico(cd_prescritor_w, cd_especialidade) = 'S')) 
		and	(((coalesce(ie_tipo_item,'TOD') = 'SOL') and (ie_agrupador_w = 4)) or 
			 ((coalesce(ie_tipo_item,'TOD') = 'OUT') and (ie_agrupador_w <> 4)) or (coalesce(ie_tipo_item,'TOD') = 'TOD'))	 
		and	ie_tipo								= '2';
 
		/* Caso haja alguma regra, faz as consistências */
 
		if (qt_regra_w > 0) then 
			open c01;
			loop 
			fetch c01 into 
				qt_limite_pessoa_w, 
				ie_dose_limite_w, 
				cd_unid_med_limite_w, 
				ie_justificativa_w;		
			EXIT WHEN NOT FOUND; /* apply on c01 */
				begin 
	 
				select	coalesce(qt_max_prescricao,0), 
					cd_unidade_medida_consumo 
				into STRICT	qt_max_prescricao_w, 
					cd_unidade_medida_consumo_w 
				from	material 
				where	cd_material	= cd_material_w;
 
				if (cd_unidade_medida_consumo_w = cd_unidade_medida_dose_w) then 
					qt_conversao_dose_w	:= 1;
				else 
					begin 
					select	coalesce(max(qt_conversao),1) 
					into STRICT	qt_conversao_dose_w 
					from	material_conversao_unidade 
					where	cd_material		= cd_material_w 
					and	cd_unidade_medida	= cd_unidade_medida_dose_w;
					exception	 
						when others then 
							qt_conversao_dose_w	:= 1;
					end;
				end if;
	 
				qt_dose_w	:= (trunc(qt_dose_ww * 1000 / qt_conversao_dose_w)/ 1000);
	 
				if (qt_max_prescricao_w > 0) and (qt_max_prescricao_w < coalesce(qt_dose_w,0)) then 
					ds_erro_p	:= wheb_mensagem_pck.get_texto(277286, 'QT_MAX_PRESCRICAO_P=' || qt_max_prescricao_w || ';CD_UNIDADE_MEDIDA_CONSUMO_P=' || cd_unidade_medida_consumo_w);
				end if;
 
				if (cd_unidade_medida_consumo_w = cd_unid_med_limite_w) then 
					qt_conversao_dose_limite_w	:= 1;
				else 
					begin 
					select	coalesce(max(qt_conversao),1) 
					into STRICT	qt_conversao_dose_limite_w 
					from	material_conversao_unidade 
					where	cd_material		= cd_material_w 
					and	cd_unidade_medida	= cd_unid_med_limite_w;
					exception 
						when others then 
							qt_conversao_dose_limite_w	:= 1;
					end;
				end if;
 
				qt_dose_w		:= (trunc(qt_dose_ww * 1000 / qt_conversao_dose_w)/ 1000);
				qt_dose_limite_w	:= (trunc(qt_limite_pessoa_w * 1000 / qt_conversao_dose_limite_w)/ 1000);				
								 
				if (ie_dose_limite_w = 'DIA') then 
					if (nr_horas_validade_w	> 24) then 
						nr_ocorrencia_w	:= trunc(((nr_ocorrencia_w * 24) / nr_horas_validade_w));
					end if;
					qt_dose_w	:= qt_dose_w * coalesce(nr_ocorrencia_w,1);
					qt_dose_w	:= qt_dose_w + obter_dose_medic_dia(nr_prescricao_p,nr_sequencia_p,ie_utiliza_horarios_p,cd_material_w);
				elsif (qt_peso_w > 0) and (ie_dose_limite_w = 'KG') then 
					qt_dose_w	:= qt_dose_w / coalesce(qt_peso_w,1);
				elsif (ie_dose_limite_w = 'AT') then 
					begin 
					qt_dose_w	:= qt_dose_w * coalesce(nr_ocorrencia_w,1);
					qt_dose_w	:= qt_dose_w + obter_dose_medic_atend_dia(nr_prescricao_p,nr_sequencia_p,qt_dose_w,ie_utiliza_horarios_p,cd_material_w);
					end;
				elsif (qt_peso_w > 0) and (ie_dose_limite_w = 'KG/DIA') then 
					begin 
					/*por KG*/
 
					qt_dose_w	:= qt_dose_w / coalesce(qt_peso_w,1);					
					/*por dia*/
 
					if (nr_horas_validade_w	> 24) then 
						nr_ocorrencia_w	:= trunc(((nr_ocorrencia_w * 24) / nr_horas_validade_w));
					end if;
					qt_dose_w	:= qt_dose_w * coalesce(nr_ocorrencia_w,1);
					qt_dose_w	:= qt_dose_w + obter_dose_medic_dia(nr_prescricao_p,nr_sequencia_p,ie_utiliza_horarios_p,cd_material_w);
					end;
				elsif (ie_dose_limite_w = 'KG/D') and (qt_peso_w > 0)then 
					qt_dose_w	:= qt_dose_w / coalesce(qt_peso_w,1);/*por KG*/
					qt_dose_w	:= qt_dose_w + obter_dose_medic_dia(nr_prescricao_p,nr_sequencia_p,ie_utiliza_horarios_p,cd_material_w);
				end if;		
 
				if (qt_dose_limite_w > 0) and (qt_dose_limite_w < coalesce(qt_dose_w,0)) and 
					((ie_justificativa_w = 'S') or (coalesce(ds_justificativa_w::text, '') = '')) then 
					ds_dose_limite_w	:= obter_valor_dominio(1851, ie_dose_limite_w);
					ds_erro2_p := wheb_mensagem_pck.get_texto(277256, 'QT_LIMITE_PESSOA_P=' || qt_limite_pessoa_w || ';CD_UNID_MED_LIMITE_P=' || cd_unid_med_limite_w || ';DS_DOSE_LIMITE_P=' || lower(ds_dose_limite_w));
				end if;
 
				end;
			end loop;
			close c01;
		end if;
		 
		end;
	end if;
	end;	
end if;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE vipe_verifica_dose_maxima ( nr_prescricao_p bigint, nr_sequencia_p bigint, ie_utiliza_horarios_p text, qt_dose_p bigint, ds_erro_p INOUT text, ds_erro2_p INOUT text) FROM PUBLIC;

