CREATE OR REPLACE PROCEDURE wdlg_ok_hist_alteracao (cd_pessoa_fisica_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_w bigint;
ie_func_sem_local_w varchar(1);


BEGIN

ie_func_sem_local_w := obter_valor_param_usuario(6001, 130, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento);

