CREATE OR REPLACE PROCEDURE wl_alterar_fim_previsto (nr_seq_worklist_p bigint, dt_final_previsto_p timestamp, dt_final_real_p timestamp, nm_usuario_p text, ds_motivo_p text, NR_SEQ_MOTIVO_P bigint) AS $body$
BEGIN
if (nr_seq_worklist_p IS NOT NULL AND nr_seq_worklist_p::text <> '') and (dt_final_previsto_p IS NOT NULL AND dt_final_previsto_p::text <> '') then
	-- inseri historico de status do worklist
	insert into wl_worklist_historico(nr_sequencia,
             nr_seq_worklist, 
             ds_motivo, 
             nm_usuario, 
             dt_atualizacao,
             nr_seq_motivo) 
	values (nextval('wl_worklist_historico_seq'), 
             nr_seq_worklist_p, 
             ds_motivo_p, 
             nm_usuario_p, 
             clock_timestamp(),
             NR_SEQ_MOTIVO_P);

	-- atualiza worklist
	update wl_worklist 
	set    dt_final_previsto = dt_final_previsto_p, 
		   dt_final_real = dt_final_real_p, 
		   nm_usuario = nm_usuario_p, 
		   dt_atualizacao = clock_timestamp() 
	where  nr_sequencia = nr_seq_worklist_p;

end if;

commit;

end;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
