CREATE OR REPLACE PROCEDURE wsuite_add_notification ( ie_notification_type_p text, nr_primary_key_p bigint, nr_auxiliary_key_p bigint, nm_usuario_p text, cd_pessoa_fisica_p text, nr_seq_prescr_proc_p bigint) AS $body$
DECLARE


qt_user_w bigint;


BEGIN

select count(*)
into STRICT qt_user_w
from wsuite_usuario a
where a.cd_pessoa_fisica = cd_pessoa_fisica_p
or   exists (SELECT 1
    from pessoa_fisica_dependente b
    where b.cd_pessoa_fisica = a.cd_pessoa_fisica
    and  b.cd_pessoa_dependente = cd_pessoa_fisica_p);

if (ie_notification_type_p IS NOT NULL AND ie_notification_type_p::text <> '') and (qt_user_w > 0) then

 insert into WSUITE_NOTIFICATION(nr_sequencia,
         dt_atualizacao,
         nm_usuario,
         dt_atualizacao_nrec,
         nm_usuario_nrec,
         nr_primary_key,
         nr_auxiliary_key,
         dt_notification,
         ie_notification_type,
         ie_notification_status,
         cd_pessoa_fisica,
         nr_seq_prescr_proc)
       values (nextval('wsuite_notification_seq'),
         clock_timestamp(),
         nm_usuario_p,
         clock_timestamp(),
         nm_usuario_p,
         nr_primary_key_p,
         nr_auxiliary_key_p,
         clock_timestamp(),
         ie_notification_type_p,
         'U',
         cd_pessoa_fisica_p,
         nr_seq_prescr_proc_p);

end if;

--commit;
end;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
