SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE wsuite_add_rev_notification ( nm_usuario_p text, cd_pessoa_fisica_p text) AS $body$
DECLARE


vl_param_14_w			varchar(255);
dt_last_update_pf_w		timestamp;
dt_last_update_compl_w		timestamp;
qt_days_last_update_w		bigint;
nr_seq_last_unread_not_w	wsuite_notification.nr_sequencia%type;
nr_seq_last_unread_rev_not_w	wsuite_notification.nr_sequencia%type;


BEGIN

select	tws_obter_param_tipo_login('1', '9111' ,'14')
into STRICT	vl_param_14_w
;

if (vl_param_14_w IS NOT NULL AND vl_param_14_w::text <> '') and (obter_se_somente_numero(vl_param_14_w) = 'S') then

	select	max(nr_sequencia)
	into STRICT	nr_seq_last_unread_rev_not_w
	from	wsuite_notification a
	where	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	a.ie_notification_type = 'RC'
	and	a.ie_notification_status in ('U', 'V');

	select	max(nr_sequencia)
	into STRICT	nr_seq_last_unread_not_w
	from	wsuite_notification a
	where	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	a.ie_notification_type <> 'RC'
	and	a.ie_notification_status in ('U', 'V');
	
	if (coalesce(nr_seq_last_unread_rev_not_w::text, '') = '') or (nr_seq_last_unread_not_w > nr_seq_last_unread_rev_not_w) then

		if (nr_seq_last_unread_not_w > nr_seq_last_unread_rev_not_w) then
			update	wsuite_notification
			set	ie_notification_status = 'R'
			where	cd_pessoa_fisica = cd_pessoa_fisica_p
			and	ie_notification_type = 'RC'
			and	ie_notification_status in ('U', 'V');
			commit;
		end if;
	
		select	max(dt_atualizacao)
		into STRICT	dt_last_update_pf_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;

		if (dt_last_update_pf_w IS NOT NULL AND dt_last_update_pf_w::text <> '') then
			select	max(dt_atualizacao)
			into STRICT	dt_last_update_compl_w
			from	compl_pessoa_fisica
			where	cd_pessoa_fisica = cd_pessoa_fisica_p
			and	ie_tipo_complemento = 1;

			if (coalesce(dt_last_update_compl_w::text, '') = '') then
				select	trunc(clock_timestamp() - dt_last_update_pf_w)
				into STRICT	qt_days_last_update_w
				;
			else
				select	trunc(clock_timestamp() - greatest(dt_last_update_pf_w, dt_last_update_compl_w))
				into STRICT	qt_days_last_update_w
				;
			end if;

			if (qt_days_last_update_w >= somente_numero(vl_param_14_w)) then
				CALL wsuite_add_notification('RC', null, null, nm_usuario_p, cd_pessoa_fisica_p, null);
				commit;
			end if;
		end if;
	else
		update	wsuite_notification
		set	ie_notification_status = 'U'
		where	nr_sequencia = nr_seq_last_unread_rev_not_w
		and	ie_notification_status in ('R', 'V');
		commit;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
