SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE wsuite_cancel_pre_schedule (NR_SEQ_PRE_SCHEDULE_P WSUITE_PRE_SCHEDULE.NR_SEQUENCIA%type, IE_STATUS_P WSUITE_PRE_SCHEDULE.IE_STATUS%type, NM_USUARIO_P WSUITE_PRE_SCHEDULE.NM_USUARIO%type, DS_JUSTIFICATIVA_P WSUITE_PRE_SCHEDULE.DS_JUSTIFICATIVA%type) AS $body$
DECLARE


IE_STATUS_W		WSUITE_PRE_SCHEDULE.IE_STATUS%type;


BEGIN

/*----------------------------------
Procedure to update the status of appointmnet request from web suite.
Used in scheduling function.
-------------------------------------
IE_STATUS input values
  R - Requested
  P - Processing
  C - Completed
  X - Cancelled
Refer domain 8778 for all values.
*/
	--Somente entra para o processo de cancelamento, caso o registro esteja como "Solicitado".
	select max(IE_STATUS)
	into STRICT IE_STATUS_W
	from WSUITE_PRE_SCHEDULE
	WHERE NR_SEQUENCIA = NR_SEQ_PRE_SCHEDULE_P;

	if (IE_STATUS_W = 'R') then

		--Coloca o status para "Em processamento" para que ninguem mais utilize durante o processo.
		CALL TWS_PRE_SCHEDULE_STATUS_UPDATE(NR_SEQ_PRE_SCHEDULE_P,'P',NM_USUARIO_P,null);

		begin
			--Coloca o status para "Cancelado".
			CALL TWS_PRE_SCHEDULE_STATUS_UPDATE(NR_SEQ_PRE_SCHEDULE_P,'X',NM_USUARIO_P,DS_JUSTIFICATIVA_P);
		exception
		when others then
			--Se ocorrer erro, volta o status para "Solicitado".
			CALL TWS_PRE_SCHEDULE_STATUS_UPDATE(NR_SEQ_PRE_SCHEDULE_P,'R',NM_USUARIO_P,DS_JUSTIFICATIVA_P);
		end;

		Commit;

	end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
