SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE wsuite_liberar_termo ( nr_seq_termo_p wsuite_termo_uso.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, ie_tipo_acao_p text, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE

				
qt_termo_w			bigint;
ie_tipo_termo_w			wsuite_termo_uso.ie_tipo_termo%type;
ie_aplicacao_w			wsuite_termo_uso.ie_aplicacao%type;
ie_aplicacao_ant_w		wsuite_termo_uso.ie_aplicacao_ant%type;

BEGIN

if (ie_tipo_acao_p = 'L') then

	select	ie_tipo_termo,
		ie_aplicacao,
		ie_aplicacao_ant
	into STRICT	ie_tipo_termo_w,
		ie_aplicacao_w,
		ie_aplicacao_ant_w
	from	wsuite_termo_uso
	where	nr_sequencia = nr_seq_termo_p;
	
	if (ie_tipo_termo_w = 'AP' and coalesce(ie_aplicacao_ant_w::text, '') = '' and coalesce(ie_aplicacao_w::text, '') = '') then
		select	count(1)
		into STRICT	qt_termo_w
		from	wsuite_termo_uso
		where	coalesce(ie_aplicacao::text, '') = ''
		and	coalesce(ie_aplicacao_ant::text, '') = ''
		and	cd_estabelecimento = cd_estabelecimento_p
		and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
		and	ie_situacao = 'A'
		and	ie_tipo_termo = ie_tipo_termo_w;
	end if;

	if (ie_aplicacao_w IS NOT NULL AND ie_aplicacao_w::text <> '') then
		select	count(1)
		into STRICT	qt_termo_w
		from	wsuite_termo_uso
		where	ie_aplicacao = ie_aplicacao_w
		and	cd_estabelecimento = cd_estabelecimento_p
		and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
		and	ie_situacao = 'A'
		and	ie_tipo_termo = ie_tipo_termo_w;
		
	elsif (ie_aplicacao_ant_w IS NOT NULL AND ie_aplicacao_ant_w::text <> '') then
		select	count(1)
		into STRICT	qt_termo_w
		from	wsuite_termo_uso
		where	ie_aplicacao_ant = ie_aplicacao_ant_w
		and	cd_estabelecimento = cd_estabelecimento_p
		and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
		and	ie_situacao = 'A'
		and	ie_tipo_termo = ie_tipo_termo_w;
	end if;

	if (qt_termo_w > 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1119767);
	else
		update	wsuite_termo_uso
		set	dt_liberacao 	= clock_timestamp(),
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where  	nr_sequencia 	= nr_seq_termo_p;
	end if;

elsif (ie_tipo_acao_p = 'I') then
	update	wsuite_termo_uso
	set	ie_situacao 	= 'I',
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where  	nr_sequencia 	= nr_seq_termo_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
