SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE wsuite_set_pf_priv_data ( cd_pessoa_fisica_p pessoa_fisica_privacidade.cd_pessoa_fisica%type, nm_usuario_p pessoa_fisica_privacidade.nm_usuario%type, nr_seq_pf_privacidade_p pessoa_fisica_privacidade.nr_sequencia%type, nr_seq_pf_privac_item_p pessoa_fisica_privac_item.nr_sequencia%type, ie_aceite_p pessoa_fisica_privac_item.ie_aceite%type, ie_commit_p text) AS $body$
DECLARE


dt_liberacao_w			pessoa_fisica_privacidade.dt_liberacao%type;
nr_seq_pf_privacidade_w		pessoa_fisica_privacidade.nr_sequencia%type;
ie_tipo_consentimento_old_w	pessoa_fisica_privac_item.ie_tipo_consentimento%type;
ie_tipo_documento_old_w		pessoa_fisica_privac_item.ie_tipo_documento%type;
ie_tipo_complemento_pf_old_w	pessoa_fisica_privac_item.ie_tipo_complemento_pf%type;
nr_seq_compl_pf_old_w		pessoa_fisica_privac_item.nr_seq_compl_pf%type;


BEGIN

if (ie_aceite_p IS NOT NULL AND ie_aceite_p::text <> '') then
	if (coalesce(nr_seq_pf_privacidade_p::text, '') = '') then
		select	max(a.dt_liberacao)
		into STRICT	dt_liberacao_w
		from	pessoa_fisica_privac_item b,
			pessoa_fisica_privacidade a
		where	b.nr_seq_pf_privacidade	= a.nr_sequencia
		and	b.nr_sequencia = nr_seq_pf_privac_item_p;
	else
		select	max(a.dt_liberacao)
		into STRICT	dt_liberacao_w
		from	pessoa_fisica_privacidade a
		where	a.nr_sequencia = nr_seq_pf_privacidade_p;
	end if;	

	if (coalesce(dt_liberacao_w::text, '') = '') then
		nr_seq_pf_privacidade_w := nr_seq_pf_privacidade_p;
	else
		select	max(nr_sequencia)
		into STRICT	nr_seq_pf_privacidade_w
		from	pessoa_fisica_privacidade a
		where	a.cd_pessoa_fisica = cd_pessoa_fisica_p
		and	coalesce(a.ie_situacao,'A') = 'A'
		and	coalesce(a.dt_liberacao::text, '') = ''
		and	a.nm_usuario = nm_usuario_p
		and	coalesce(a.dt_inativacao::text, '') = '';

		if (coalesce(nr_seq_pf_privacidade_w::text, '') = '') then
			insert into pessoa_fisica_privacidade(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				cd_pessoa_fisica,
				ie_situacao,
				dt_criacao)
			values (
				nextval('pessoa_fisica_privacidade_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				cd_pessoa_fisica_p,
				'A',
				clock_timestamp())
			returning nr_sequencia
			into nr_seq_pf_privacidade_w;
		end if;
	end if;

	select	a.ie_tipo_consentimento,
		a.ie_tipo_documento,
		a.ie_tipo_complemento_pf,
		a.nr_seq_compl_pf
	into STRICT	ie_tipo_consentimento_old_w,
		ie_tipo_documento_old_w,
		ie_tipo_complemento_pf_old_w,
		nr_seq_compl_pf_old_w
	from	pessoa_fisica_privac_item a
	where	a.nr_sequencia = nr_seq_pf_privac_item_p;

	update	pessoa_fisica_privac_item a
	set	a.ie_aceite = ie_aceite_p,
		a.nm_usuario = nm_usuario_p,
		a.dt_atualizacao = clock_timestamp()
	where	a.nr_seq_pf_privacidade = nr_seq_pf_privacidade_w
	and	a.ie_tipo_consentimento = ie_tipo_consentimento_old_w
	and	coalesce(a.ie_tipo_documento,'#!@$') = coalesce(ie_tipo_documento_old_w,'#!@$')
	and	coalesce(a.ie_tipo_complemento_pf,-1) = coalesce(ie_tipo_complemento_pf_old_w,-1)
	and	coalesce(a.nr_seq_compl_pf,-1) = coalesce(nr_seq_compl_pf_old_w,-1);
end if;

if (coalesce(ie_commit_p,'N') = 'S') then
	commit;
	CALL liberar_pf_privacidade(nr_seq_pf_privacidade_w, nm_usuario_p);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE wsuite_set_pf_priv_data ( cd_pessoa_fisica_p pessoa_fisica_privacidade.cd_pessoa_fisica%type, nm_usuario_p pessoa_fisica_privacidade.nm_usuario%type, nr_seq_pf_privacidade_p pessoa_fisica_privacidade.nr_sequencia%type, nr_seq_pf_privac_item_p pessoa_fisica_privac_item.nr_sequencia%type, ie_aceite_p pessoa_fisica_privac_item.ie_aceite%type, ie_commit_p text) FROM PUBLIC;

