CREATE OR REPLACE PROCEDURE wsuite_transf_agendamento ( lista_consulta_p text, lista_exame_p text, cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_ageint_p out bigint) is ds_lista_w varchar(4000) AS $body$
DECLARE

	nr_seq_retorno_w	agenda_integrada_item.nr_sequencia%type;
	tam_lista_w			bigint;
	ie_pos_virgula_w	bigint;

BEGIN
	tam_lista_w	 := length(ds_lista_w);
	ie_pos_virgula_w := position(',' in ds_lista_w);

	if (ie_pos_virgula_w <> 0) then
		nr_seq_retorno_w	:= (substr(ds_lista_w,1,(ie_pos_virgula_w - 1)))::numeric;
		ds_lista_w		:= substr(ds_lista_w,(ie_pos_virgula_w + 1),tam_lista_w);
	end if;
	return;
end;

function obter_lista_consulta(ds_lista_consulta_p	Varchar2) return;
nr_seq_consulta_w		agenda_consulta.nr_sequencia%type;
ds_lista_item_cons_w	Varchar2(4000) := null;
begin
	ds_lista_w	:= ds_lista_consulta_p;
	while(ds_lista_w IS NOT NULL AND ds_lista_w::text <> '') loop
		nr_seq_consulta_w := atualizar_seq_item_lista;
		select 	max(nr_sequencia)
		into STRICT	nr_seq_ageint_item_w
		from	agenda_integrada_item
		where	nr_seq_agenda_cons = nr_seq_consulta_w;
		if (nr_seq_ageint_item_w IS NOT NULL AND nr_seq_ageint_item_w::text <> '') then
			ds_lista_item_cons_w := ds_lista_item_cons_w || nr_seq_ageint_item_w || ',';
		end if;
	end loop;
	return;
end;

function obter_lista_exame(ds_lista_exame_p	Varchar2) return;
nr_seq_exame_w			agenda_paciente.nr_sequencia%type;
ds_lista_item_exame_w	Varchar2(4000) := null;
begin
	ds_lista_w	:= ds_lista_exame_p;
	while(ds_lista_w IS NOT NULL AND ds_lista_w::text <> '') loop
		nr_seq_exame_w := atualizar_seq_item_lista;
		select 	max(nr_sequencia)
		into STRICT	nr_seq_ageint_item_w
		from	agenda_integrada_item
		where	nr_seq_agenda_exame = nr_seq_exame_w;
		
		if (nr_seq_ageint_item_w IS NOT NULL AND nr_seq_ageint_item_w::text <> '') then
			ds_lista_item_exame_w := ds_lista_item_exame_w || nr_seq_ageint_item_w || ',';
		end if;
	end loop;
	return;
end;

begin

if (lista_consulta_p IS NOT NULL AND lista_consulta_p::text <> '') then
	ds_lista_ageint_item_w := obter_lista_consulta(lista_consulta_p);
end if;

if (lista_exame_p IS NOT NULL AND lista_exame_p::text <> '') then
	ds_lista_ageint_item_w := ds_lista_ageint_item_w || obter_lista_exame(lista_exame_p);
end if;

if (ds_lista_ageint_item_w IS NOT NULL AND ds_lista_ageint_item_w::text <> '') then
	nr_seq_ageint_p := ageint_multitransferencia_item(ds_lista_ageint_item_w, cd_estabelecimento_p, nm_usuario_p);
end if;

end;
$body$
