SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ws_liberar_prescricao ( nr_prescricao_p bigint, ie_tipo_pessoa_p text, cd_perfil_p bigint, nm_usuario_p text, cd_erro_p INOUT text, ds_erro_p INOUT text) AS $body$
DECLARE

 
nr_atendimento_w	atendimento_paciente.nr_atendimento%type;
ie_existe_w			varchar(1);

/* Códigos de erro 
0 - SUCESSO 
1 - Dados não informados 
	1.1 - Prescrição não informado 
	1.2 - Tipo de pessoa não informada 
	1.3 - Perfil não informado 
2 - Dados não encontrados 
	2.1 - Prescrição não encontrada 
	2.2 - 	Atendimento não encontrado para a prescrição 
3 - Erro ao liberar a prescrição 
*/
 
 

BEGIN 
 
cd_erro_p	:= 0;
 
if (coalesce(nr_prescricao_p::text, '') = '') then 
	cd_erro_p	:= 1.1;
	ds_erro_p	:= WHEB_MENSAGEM_PCK.get_texto(277599,null);
elsif (coalesce(ie_tipo_pessoa_p::text, '') = '') then 
	cd_erro_p	:= 1.2;
	ds_erro_p	:= WHEB_MENSAGEM_PCK.get_texto(278500,null);
elsif (coalesce(cd_perfil_p::text, '') = '') then 
	cd_erro_p	:= 1.3;
	ds_erro_p	:= WHEB_MENSAGEM_PCK.get_texto(278502,null);
end if;
 
if (cd_erro_p = 0) then 
 
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
	into STRICT	ie_existe_w 
	from	prescr_medica a 
	where	a.nr_prescricao = nr_prescricao_p;
	 
	if (ie_existe_w = 'N') then 
		cd_erro_p	:=	2.1;
		ds_erro_p	:= WHEB_MENSAGEM_PCK.get_texto(277599,null);
	else 
		select	max(a.nr_atendimento) 
		into STRICT	nr_atendimento_w 
		from	prescr_medica a 
		where	a.nr_prescricao = nr_prescricao_p;
		 
		if (coalesce(nr_atendimento_w::text, '') = '') then 
			cd_erro_p	:=	2.2;
			ds_erro_p	:= WHEB_MENSAGEM_PCK.get_texto(278505,null)||to_char(nr_prescricao_p);
		else 
			begin 
			ds_erro_p := liberar_prescricao(	nr_prescricao_p, nr_atendimento_w, ie_tipo_pessoa_p, cd_perfil_p, nm_usuario_p, 'N', ds_erro_p);
			exception 
				when others then 
				ds_erro_p	:= WHEB_MENSAGEM_PCK.get_texto(278507,null)||substr(sqlerrm,1,255);
				end;
									 
			if (ds_erro_p IS NOT NULL AND ds_erro_p::text <> '') then 
				cd_erro_p	:= 3;
			end if;
		end if;
	end if;	
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
