SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ws_suspender_exame_prescricao ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, ds_motivo_p text, nm_usuario_p text, cd_erro_p INOUT text, ds_erro_p INOUT text) AS $body$
DECLARE

 
ie_erro_w		varchar(1);
											
/* Códigos de erro 
0 - SUCESSO 
1 - Dados não informados 
	1.1 - Prescrição não informada 
	1.2 - Sequência da prescrição não informada 
2 - Dados não encontrados 
*/
 
											 

BEGIN 
 
cd_erro_p	:= 0;
 
if (coalesce(nr_prescricao_p::text, '') = '') then 
	cd_erro_p	:= 1.1;
	ds_erro_p	:= WHEB_MENSAGEM_PCK.get_texto(277599,null);
elsif (coalesce(nr_seq_prescr_p::text, '') = '') then 
	cd_erro_p	:= 1.2;
	ds_erro_p	:= WHEB_MENSAGEM_PCK.get_texto(277600,null);
end if;
 
if (cd_erro_p = 0) then 
	 
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
	into STRICT	ie_erro_w 
	from	prescr_procedimento a 
	where	a.nr_prescricao = nr_prescricao_p 
	and	a.nr_sequencia = nr_seq_prescr_p;
	 
	if (ie_erro_w = 'N') then 
		cd_erro_p	:= 2.1;
		ds_erro_p	:= WHEB_MENSAGEM_PCK.get_texto(278764,null);
	else 
		begin 
		CALL Suspender_item_Prescricao(nr_prescricao_p, 
					nr_seq_prescr_p, 
					null, 
					ds_motivo_p, 
					'PRESCR_PROCEDIMENTO', 
					nm_usuario_p);
		exception 
		when others then 
			cd_erro_p	:= 3;
			ds_erro_p	:= substr(WHEB_MENSAGEM_PCK.get_texto(278088,'DS_ERRO='||sqlerrm),1,2000);
		end;
	end if;
	 
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
