SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE w_gerar_mat_estoque_critico ( cd_estabelecimento_p bigint, cd_local_estoque_p bigint, cd_grupo_material_p bigint, cd_subgrupo_material_p bigint, cd_classe_material_p bigint, cd_material_p bigint, ie_tipo_p bigint, ie_disponivel_mercado_p text, nm_usuario_p text) AS $body$
DECLARE


cd_material_w		integer;
qt_estoque_minimo_w	double precision;
qt_estoque_maximo_w	double precision;
qt_ponto_pedido_w		double precision;
qt_consumo_mensal_w	double precision;
qt_disp_estoque_w		double precision;
dt_mesano_referencia_w	timestamp;
ie_curva_abc_w		varchar(1);
ie_curva_xyz_w		varchar(1);

c01 CURSOR FOR
SELECT	a.cd_material_estoque,
	coalesce(max(b.qt_estoque_minimo),0),
	coalesce(max(b.qt_estoque_maximo),0),
	coalesce(max(b.qt_ponto_pedido),0),
	coalesce(max(b.qt_consumo_mensal),0),
	max(b.ie_curva_xyz)
from	material a,
	material_estab b,
	estrutura_material_v e
where	a.cd_material = b.cd_material
and	a.cd_material = e.cd_material
and (coalesce(cd_grupo_material_p,0) 		= 0 or cd_grupo_material_p 		= e.cd_grupo_material)
and (coalesce(cd_subgrupo_material_p,0) 	= 0 or cd_subgrupo_material_p 	= e.cd_subgrupo_material)
and (coalesce(cd_classe_material_p,0) 		= 0 or cd_classe_material_p 		= e.cd_classe_material)
and (coalesce(cd_material_p,0) 		= 0 or cd_material_p 		= e.cd_material)
and (coalesce(ie_disponivel_mercado_p,'0') 	= '0' or a.ie_disponivel_mercado 	= ie_disponivel_mercado_p)
and	b.cd_estabelecimento 		= cd_estabelecimento_p
and	a.ie_situacao = 'A'
and	b.ie_ressuprimento = 'S'
and	b.ie_material_estoque = 'S'
and	b.ie_padronizado = 'S'
group by a.cd_material_estoque;


BEGIN

delete	FROM w_saldo_estoque_critico
where	nm_usuario = nm_usuario_p;

select	coalesce(max(dt_mesano_vigente),trunc(clock_timestamp(),'mm'))
into STRICT	dt_mesano_referencia_w
from	parametro_estoque
where	cd_estabelecimento = cd_estabelecimento_p;

open c01;
loop
fetch c01 into
	cd_material_w,
	qt_estoque_minimo_w,
	qt_estoque_maximo_w,
	qt_ponto_pedido_w,
	qt_consumo_mensal_w,
	ie_curva_xyz_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (cd_local_estoque_p > 0) then
		qt_disp_estoque_w := obter_saldo_disp_estoque(cd_estabelecimento_p, cd_material_w, cd_local_estoque_p, dt_mesano_referencia_w);
	else
		qt_disp_estoque_w := obter_saldo_disp_estoque(cd_estabelecimento_p, cd_material_w, null, dt_mesano_referencia_w);
	end if;

	if	((ie_tipo_p = 0) and (qt_disp_estoque_w <= qt_ponto_pedido_w) and (qt_ponto_pedido_w > 0)) or
		((ie_tipo_p = 1) and (qt_disp_estoque_w > qt_estoque_maximo_w) and (qt_estoque_maximo_w > 0)) or
		((ie_tipo_p = 2) and (qt_disp_estoque_w <= qt_estoque_minimo_w) and (qt_estoque_minimo_w > 0)) or
		((ie_tipo_p = 3) and (qt_disp_estoque_w <= qt_ponto_pedido_w AND qt_ponto_pedido_w > 0) and (qt_disp_estoque_w > qt_estoque_minimo_w AND qt_estoque_minimo_w > 0)) then
		begin
		ie_curva_abc_w	:= obter_curva_abc_estab(cd_estabelecimento_p, cd_material_w, 'N', dt_mesano_referencia_w);

		insert into w_saldo_estoque_critico(
			nr_sequencia,
			cd_material,
			qt_estoque_minimo,
			qt_estoque_maximo,
			qt_ponto_pedido,
			qt_consumo_mensal,
			qt_disp_estoque,
			ie_curva_abc,
			ie_curva_xyz,
			nm_usuario,
			dt_atualizacao)
		values (nextval('w_saldo_estoque_critico_seq'),
			cd_material_w,
			qt_estoque_minimo_w,
			qt_estoque_maximo_w,
			qt_ponto_pedido_w,
			qt_consumo_mensal_w,
			qt_disp_estoque_w,
			ie_curva_abc_w,
			ie_curva_xyz_w,
			nm_usuario_p,
			clock_timestamp());
		end;
	end if;
	end;
end loop;
close c01;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
