CREATE OR REPLACE PROCEDURE w_mprev_atualiza_ag_ativ_item ( nr_seq_w_agendamento_ativ_p w_mprev_agendamento_ativ.nr_sequencia%type, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Insere os registros na tabela w_mprev_ag_ativ_item correspondentes a atividade
selecionada na tabela  w_mprev_agendamento_ativ
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [ ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ie_executado_w		w_mprev_agendamento_ativ.ie_executado%type;
nr_seq_ag_ativ_item_w	w_mprev_ag_ativ_item.nr_sequencia%type;

C01 CURSOR FOR
	SELECT	c.nr_sequencia nr_seq_avaliacao,
		null nr_seq_exame,
		a.ie_executado
	from	w_mprev_agendamento_ativ a,
		mprev_atividade_plano b,
		mprev_ativ_plano_aval c
	where	a.nr_seq_atividade = b.nr_sequencia
	and	b.nr_sequencia = c.nr_seq_atividade
	and 	a.nr_sequencia = nr_seq_w_agendamento_ativ_p
	
union all

	SELECT	null nr_seq_avaliacao,
		c.nr_sequencia nr_seq_exame,
		a.ie_executado
	from	w_mprev_agendamento_ativ a,
		mprev_atividade_plano b,
		mprev_ativ_plano_exame c
	where	a.nr_seq_atividade = b.nr_sequencia
	and	b.nr_sequencia = c.nr_seq_atividade
	and 	a.nr_sequencia = nr_seq_w_agendamento_ativ_p;

BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_ag_ativ_item_w
from	w_mprev_ag_ativ_item
where	nr_seq_w_agendamento_ativ = nr_seq_w_agendamento_ativ_p;

if (coalesce(nr_seq_ag_ativ_item_w::text, '') = '') then
	for c_01 in C01 loop
		begin
			insert into w_mprev_ag_ativ_item(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_w_agendamento_ativ,
				nr_seq_ativ_plano_aval,
				nr_seq_ativ_plano_exame,
				ie_executado)
			values (
				nextval('w_mprev_ag_ativ_item_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_w_agendamento_ativ_p,
				c_01.nr_seq_avaliacao,
				c_01.nr_seq_exame,
				c_01.ie_executado);
		end;
	end loop;
else
	select	a.ie_executado
	into STRICT	ie_executado_w
	from	w_mprev_agendamento_ativ a
	where	a.nr_sequencia = nr_seq_w_agendamento_ativ_p;

	update	w_mprev_ag_ativ_item
	set	nm_usuario_nrec = nm_usuario_p,
		dt_atualizacao_nrec = clock_timestamp(),
		ie_executado = ie_executado_w
	where	nr_seq_w_agendamento_ativ = nr_seq_w_agendamento_ativ_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
