CREATE OR REPLACE PROCEDURE w_pls_relat_quant_servico ( nr_seq_grupo_p bigint, nr_seq_contrato_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_grupo_w			bigint;
nr_seq_contrato_w		bigint;
nr_seq_segurado_w		bigint;
nr_seq_conta_w			bigint;

qt_exames_benef_w		bigint;
qt_internacao_benef_w		bigint;
qt_consultas_benef_w		bigint;

qt_exames_contr_w		bigint;
qt_internacao_contr_w		bigint;
qt_consultas_contr_w		bigint;

qt_exames_tot_w			bigint;
qt_internacao_tot_w		bigint;
qt_consultas_tot_w		bigint;

qt_registros_w			bigint;
ie_tipo_conta_w			varchar(10);
ie_tipo_guia_w			varchar(10);
cd_tiss_w			varchar(10);

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_contrato		c,
		pls_contrato_grupo	b,
		pls_grupo_contrato	a
	where	b.nr_seq_contrato	= c.nr_sequencia
	and	b.nr_seq_grupo		= a.nr_sequencia
	and	((a.nr_sequencia	= nr_seq_grupo_p and (nr_seq_grupo_p IS NOT NULL AND nr_seq_grupo_p::text <> '')) or (coalesce(nr_seq_grupo_p::text, '') = ''))
	and	((c.nr_sequencia	= nr_seq_contrato_p and (nr_seq_contrato_p IS NOT NULL AND nr_seq_contrato_p::text <> '')) or (coalesce(nr_seq_contrato_p::text, '') = ''))
	group by a.nr_sequencia;

C02 CURSOR FOR
	SELECT	c.nr_sequencia
	from	pls_contrato		c,
		pls_contrato_grupo	b,
		pls_grupo_contrato	a
	where	b.nr_seq_contrato	= c.nr_sequencia
	and	b.nr_seq_grupo		= a.nr_sequencia
	and	a.nr_sequencia		= nr_seq_grupo_w
	and	((c.nr_sequencia	= nr_seq_contrato_p and (nr_seq_contrato_p IS NOT NULL AND nr_seq_contrato_p::text <> '')) or (coalesce(nr_seq_contrato_p::text, '') = ''))
	and	c.cd_estabelecimento	= cd_estabelecimento_p
	group by c.nr_sequencia;

C03 CURSOR FOR
	SELECT	b.nr_sequencia
	from	pls_segurado		b,
		pls_contrato		a
	where	b.nr_seq_contrato	= a.nr_sequencia
	and	a.nr_sequencia		= nr_seq_contrato_w
	and	coalesce(b.dt_rescisao::text, '') = ''
	and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '');

C04 CURSOR FOR
	SELECT	b.nr_sequencia,
		b.ie_tipo_guia,
		c.cd_tiss
	FROM pls_segurado a, pls_tipo_atendimento c
LEFT OUTER JOIN pls_conta b ON (c.nr_sequencia = b.nr_seq_tipo_atendimento)
WHERE b.nr_seq_segurado		= a.nr_sequencia  and a.nr_sequencia			= nr_seq_segurado_w and b.ie_status			= 'F' and trunc(b.dt_atendimento_referencia,'Month') between dt_inicial_p and dt_final_p and b.ie_tipo_guia	in ('3','5','4');



BEGIN

delete	FROM w_pls_relat_relacionamento
where	ie_tipo_relatorio = 'QS';

--pls_posicionar_sequence_cache('W_PLS_RELAT_RELACIONAMENTO','NR_SEQUENCIA','R',1000);
open C01;
loop
fetch C01 into
	nr_seq_grupo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	open C02;
	loop
	fetch C02 into
		nr_seq_contrato_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		qt_consultas_benef_w		:= 0;
		qt_internacao_benef_w		:= 0;
		qt_exames_benef_w		:= 0;

		open C03;
		loop
		fetch C03 into
			nr_seq_segurado_w;
		EXIT WHEN NOT FOUND; /* apply on C03 */
			begin

			open C04;
			loop
			fetch C04 into
				nr_seq_conta_w,
				ie_tipo_guia_w,
				cd_tiss_w;
			EXIT WHEN NOT FOUND; /* apply on C04 */
				begin

				if (ie_tipo_guia_w	= '3') then
					ie_tipo_conta_w	:= 'C';
				elsif (ie_tipo_guia_w	= '5') then
					ie_tipo_conta_w	:= 'I';
				elsif (ie_tipo_guia_w	= '4') then
					if (cd_tiss_w	= '04') then
						ie_tipo_conta_w	:= 'C';
					elsif (cd_tiss_w	= '07') then
						ie_tipo_conta_w	:= 'I';
					else
						ie_tipo_conta_w	:= 'E';
					end if;
				end if;

				select	sum(QT_PROCEDIMENTO)
				into STRICT	qt_registros_w
				from	pls_conta_proc
				where	nr_seq_conta	= nr_seq_conta_w
				and	ie_status	in ('L','S');

				if (ie_tipo_conta_w	= 'C') then
					qt_consultas_benef_w	:= qt_consultas_benef_w + coalesce(qt_registros_w,0);
				elsif (ie_tipo_conta_w	= 'I') then
					qt_internacao_benef_w	:= qt_internacao_benef_w + coalesce(qt_registros_w,0);
				elsif (ie_tipo_conta_w	= 'E') then
					qt_exames_benef_w	:= qt_exames_benef_w + coalesce(qt_registros_w,0);
				end if;

				end;
			end loop;
			close C04;

			end;
		end loop;
		close C03;

		qt_exames_contr_w	:= qt_exames_benef_w;
		qt_internacao_contr_w	:= qt_internacao_benef_w;
		qt_consultas_contr_w	:= qt_consultas_benef_w;

		insert into w_pls_relat_relacionamento(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
				nr_seq_grupo,nr_seq_contrato,qt_consulta,qt_exame,qt_internacao,
				ie_tipo_relatorio)
		values (	nextval('w_pls_relat_relacionamento_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
				nr_seq_grupo_w,nr_seq_contrato_w,qt_consultas_contr_w,qt_exames_contr_w,qt_internacao_contr_w,
				'QS');
		end;
	end loop;
	close C02;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
