SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS after_upsert_pfcs_procedure ON pfcs_procedure CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_after_upsert_pfcs_procedure() RETURNS trigger AS $BODY$
DECLARE
    nr_seq_appointment_w pfcs_appointment.nr_sequencia%type;
BEGIN
    if (coalesce(wheb_usuario_pck.get_ie_executar_trigger,PFCS_PCK_CONSTANTS.IE_YES_BR) = PFCS_PCK_CONSTANTS.IE_YES_BR) then
        select max(t.nr_sequencia)
        into STRICT nr_seq_appointment_w
        from (
            SELECT appt.nr_sequencia
            from pfcs_appointment appt
            where appt.cd_reason = NEW.cd_coding
            and appt.nr_seq_patient = NEW.nr_seq_patient
            order by appt.dt_atualizacao desc
        ) t LIMIT 1;

        if (nr_seq_appointment_w is not null) then
            update pfcs_appointment appt
            set appt.nr_seq_procedure = NEW.nr_sequencia, appt.dt_atualizacao = LOCALTIMESTAMP
            where appt.nr_sequencia = nr_seq_appointment_w
                and ((appt.nr_seq_procedure <> NEW.nr_sequencia) or (appt.nr_seq_procedure is null));
        end if;
    end if;
RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_after_upsert_pfcs_procedure() FROM PUBLIC;

CREATE TRIGGER after_upsert_pfcs_procedure
	AFTER INSERT OR UPDATE ON pfcs_procedure FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_after_upsert_pfcs_procedure();

