CREATE OR REPLACE FUNCTION trigger_fct_agenda_bloqueio_atual() RETURNS trigger AS $BODY$
DECLARE
dt_atualizacao 	timestamp	:= LOCALTIMESTAMP;
BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'N') then
    RETURN NEW;
end if;

if (ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(NEW.dt_final) < ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(NEW.dt_inicial)) then
	CALL wheb_mensagem_pck.Exibir_Mensagem_Abort(212526);
end if;

if (ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(NEW.dt_final) > ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(LOCALTIMESTAMP + interval '730 days')) or (ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(OLD.dt_final) > ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(LOCALTIMESTAMP + interval '730 days'))then
	CALL wheb_mensagem_pck.Exibir_Mensagem_Abort(355087);
end if;

RETURN NEW;
END;
$BODY$
