SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS agenda_classif_delete ON agenda_classif CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_agenda_classif_delete() RETURNS trigger AS $BODY$
declare
qt_registros_w		numeric(20) 		:= 0;
ds_consistencia_w	varchar(255)	:= null;

BEGIN
if (qt_registros_w = 0) and (ds_consistencia_w is null)then
	BEGIN
	select	count(*)
	into STRICT	qt_registros_w
	from	agenda_turno_classif
	where	cd_classificacao = OLD.cd_classificacao;

	if (qt_registros_w > 0)then
		ds_consistencia_w	:= 'AGENDA_TURNO_CLASSIF';
	end if;

	if (qt_registros_w = 0) and (ds_consistencia_w is null)then
		select	count(*)
		into STRICT	qt_registros_w
		from	agenda_servico_turno_esp
		where	ie_classif_agenda = OLD.cd_classificacao;

		if (qt_registros_w > 0)then
			ds_consistencia_w	:= 'AGENDA_SERVICO_TURNO_ESP';
		end if;

	end if;

	if (qt_registros_w = 0) and (ds_consistencia_w is null)then
		select	count(*)
		into STRICT	qt_registros_w
		from	agenda_classif_regra
		where	cd_classificacao = OLD.cd_classificacao;

		if (qt_registros_w > 0)then
			ds_consistencia_w	:= 'AGENDA_CLASSIF_REGRA';
		end if;

	end if;

	if (qt_registros_w = 0) and (ds_consistencia_w is null)then
		select	count(*)
		into STRICT	qt_registros_w
		from	med_classif_agenda
		where	ie_classif_agenda = OLD.cd_classificacao;

		if (qt_registros_w > 0)then
			ds_consistencia_w	:= 'MED_CLASSIF_AGENDA';
		end if;

	end if;

	if (qt_registros_w = 0) and (ds_consistencia_w is null)then
		select	count(*)
		into STRICT	qt_registros_w
		from	agenda_consulta
		where	ie_classif_agenda = OLD.cd_classificacao
		and		trunc(dt_agenda) >= trunc(LOCALTIMESTAMP)
		and		ie_status_agenda not in ('L', 'C', 'B', 'F', 'I', 'II', 'R');

		if (qt_registros_w > 0)then
			ds_consistencia_w	:= 'AGENDA_CONSULTA';
		end if;

	end if;

	if (qt_registros_w > 0) and (ds_consistencia_w is not null)then
		--Não é permitido excluir esta classificação, pois a mesma está sendo utilizada por algum agendamento!
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(293389);
	end if;

	end;
end if;

RETURN OLD;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_agenda_classif_delete() FROM PUBLIC;

CREATE TRIGGER agenda_classif_delete
	BEFORE DELETE ON agenda_classif FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_agenda_classif_delete();

