SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS agenda_cons_proc_after_atual ON agenda_consulta_proc CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_agenda_cons_proc_after_atual() RETURNS trigger AS $BODY$
declare

ie_gerar_consent_cons_w		varchar(1);
IE_GERAR_CONSENT_SERV_W		varchar(1);
cd_pessoa_fisica_w			AGENDA_CONSULTA.CD_PESSOA_FISICA%TYPE;
cd_tipo_agenda_w			agenda.cd_tipo_agenda%type;
cd_agenda_w					agenda.cd_agenda%type;
cd_estab_agenda_w			agenda.cd_estabelecimento%type;
ie_status_agenda_w			AGENDA_CONSULTA.ie_status_agenda%type;
cd_medico_w					AGENDA_CONSULTA.cd_medico%type;
nr_atendimento_w            agenda_consulta.nr_atendimento%type;
BEGIN
  BEGIN
	BEGIN
	SELECT ac.CD_PESSOA_FISICA,
		   ac.CD_AGENDA, 
		   a.CD_TIPO_AGENDA, 
		   a.CD_ESTABELECIMENTO,
		   ac.ie_status_agenda,
		   case when a.CD_TIPO_AGENDA = 3 then a.cd_pessoa_fisica else ac.cd_medico end cd_medico,
		   ac.nr_atendimento
	  INTO STRICT cd_pessoa_fisica_w, 
		   cd_agenda_w, 
		   cd_tipo_agenda_w, 
		   cd_estab_agenda_w,
		   ie_status_agenda_w,
		   cd_medico_w,
		   nr_atendimento_w
	  FROM AGENDA_CONSULTA ac, 
		   agenda a
	 WHERE ac.NR_SEQUENCIA = NEW.nr_seq_agenda
	   and ac.cd_agenda = a.cd_agenda;
	exception
		when others then
		null;
	end;

ie_gerar_consent_cons_w := Obter_Param_Usuario(821, 504, obter_perfil_ativo, NEW.nm_usuario, cd_estab_agenda_w, ie_gerar_consent_cons_w);
ie_gerar_consent_serv_w := Obter_Param_Usuario(866, 317, obter_perfil_ativo, NEW.nm_usuario, cd_estab_agenda_w, ie_gerar_consent_serv_w);

/*
S - Agendamento ou Proc. Adicional
N - Nao gera
A - Somente Agendamento
P - Somente Proc. Adicional
*/


	if (ie_status_agenda_w = 'N' and
		cd_pessoa_fisica_w is not null and
		((NEW.cd_procedimento is not null and (NEW.cd_procedimento <> OLD.cd_procedimento or NEW.ie_origem_proced <> OLD.ie_origem_proced or OLD.cd_procedimento is null)) or (NEW.nr_seq_proc_interno is not null and (OLD.nr_seq_proc_interno <> NEW.nr_seq_proc_interno or OLD.nr_Seq_proc_interno is null))) and
			((ie_gerar_consent_serv_w in ('S','P') and cd_tipo_agenda_w = 5) or (ie_gerar_consent_cons_w = 'S' and cd_tipo_agenda_w in (3,4) ))) then
			
			CALL agendas_inserir_consentimentos(
					cd_tipo_agenda_w,
					NEW.nr_seq_agenda,
					NEW.cd_procedimento,
					NEW.ie_origem_proced,
					NEW.nr_seq_proc_interno,
					cd_pessoa_fisica_w,
					cd_medico_w,
					cd_estab_agenda_w,
					nr_atendimento_w);
	end if;
  END;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_agenda_cons_proc_after_atual() FROM PUBLIC;

CREATE TRIGGER agenda_cons_proc_after_atual
	AFTER INSERT OR UPDATE ON agenda_consulta_proc FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_agenda_cons_proc_after_atual();

