SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS agenda_hc_paciente_atual ON agenda_hc_paciente CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_agenda_hc_paciente_atual() RETURNS trigger AS $BODY$
DECLARE

nr_controle_w			bigint;
ie_perm_dt_ret_w 		varchar(1);
cd_estabelecimento_w	smallint;
ie_cons_pac_hc_w	varchar(1);

BEGIN
ie_cons_pac_hc_w := obter_param_usuario(867, 43, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_cons_pac_hc_w);

if (wheb_usuario_pck.get_ie_executar_trigger	= 'S')  then

	select	Count(*)
	into STRICT	nr_controle_w
	from	Paciente_home_care
	where 	cd_pessoa_fisica = NEW.cd_pessoa_fisica;

	if (nr_controle_w = 0) and (ie_cons_pac_hc_w = 'S') then
		CALL wheb_mensagem_pck.Exibir_Mensagem_Abort(262781);
	end if;

	ie_perm_dt_ret_w := obter_param_usuario(867, 6, obter_perfil_ativo, NEW.nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_perm_dt_ret_w);

	If (ie_perm_dt_ret_w = 'N') and (NEW.dt_agenda < LOCALTIMESTAMP - interval '240 days'/84600 ) then
		CALL wheb_mensagem_pck.Exibir_Mensagem_Abort(262780);
	end if;

end if;
RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_agenda_hc_paciente_atual() FROM PUBLIC;

CREATE TRIGGER agenda_hc_paciente_atual
	BEFORE INSERT OR UPDATE ON agenda_hc_paciente FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_agenda_hc_paciente_atual();

