DROP TRIGGER IF EXISTS agenda_horario_adicional_atual ON agenda_horario_adicional CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_agenda_horario_adicional_atual() RETURNS trigger AS $BODY$
DECLARE
dt_atualizacao 	timestamp	:= LOCALTIMESTAMP;
BEGIN
  BEGIN
BEGIN

delete	from agenda_controle_horario
where	dt_agenda >= trunc(LOCALTIMESTAMP)
and	dt_agenda between trunc(NEW.dt_horario) and trunc(NEW.dt_horario) + 86399 / 86400
and	cd_agenda	 = NEW.cd_agenda;

exception
	when others then
      	dt_atualizacao := LOCALTIMESTAMP;
end;
  END;
RETURN NEW;
END
$BODY$
