DROP TRIGGER IF EXISTS agenda_horario_adic_delete ON agenda_horario_adicional CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_agenda_horario_adic_delete() RETURNS trigger AS $BODY$
DECLARE
dt_atualizacao 	timestamp	:= LOCALTIMESTAMP;
BEGIN
  BEGIN
BEGIN

delete	from agenda_controle_horario
where	dt_agenda >= trunc(LOCALTIMESTAMP)
and	dt_agenda between trunc(OLD.dt_horario) and trunc(OLD.dt_horario) + 86399 / 86400
and	cd_agenda	 = OLD.cd_agenda;

exception
	when others then
      	dt_atualizacao := LOCALTIMESTAMP;
end;
  END;
RETURN OLD;
END
$BODY$
