SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS agenda_horario_after ON agenda_horario CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_agenda_horario_after() RETURNS trigger AS $BODY$
declare
qt_regras_w		bigint;

BEGIN

	select	count(1)
	into STRICT	qt_regras_w
	from	agenda_regra
	where	nr_seq_turno = NEW.nr_sequencia;

	if (qt_regras_w > 0) then

		update 	agenda_regra
		set 	ie_dia_semana = NEW.dt_dia_semana,
				hr_inicio = NEW.hr_inicial,
				hr_fim = NEW.hr_final,
				dt_inicio_vigencia = NEW.dt_inicio_vigencia,
				dt_fim_vigencia = NEW.dt_final_vigencia
		where 	nr_seq_turno = NEW.nr_sequencia;

	end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_agenda_horario_after() FROM PUBLIC;

CREATE TRIGGER agenda_horario_after
	AFTER INSERT OR UPDATE ON agenda_horario FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_agenda_horario_after();

