DROP TRIGGER IF EXISTS agenda_horario_esp_after ON agenda_horario_esp CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_agenda_horario_esp_after() RETURNS trigger AS $BODY$
declare
qt_regras_w		bigint;

BEGIN

	select	count(1)
	into STRICT	qt_regras_w
	from	agenda_regra
	where	nr_seq_hor_esp = NEW.nr_sequencia;

	if (qt_regras_w > 0) then
	
		update 	agenda_regra
		set 	hr_inicio = NEW.hr_inicial,
			hr_fim = NEW.hr_final
		where 	nr_seq_hor_esp = NEW.nr_sequencia;
	
	end if;
	
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_agenda_horario_esp_after() FROM PUBLIC;

CREATE TRIGGER agenda_horario_esp_after
	AFTER INSERT OR UPDATE ON agenda_horario_esp FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_agenda_horario_esp_after();

