CREATE OR REPLACE FUNCTION trigger_fct_agenda_horario_esp_atual() RETURNS trigger AS $BODY$
DECLARE
dt_atualizacao 	timestamp	:= LOCALTIMESTAMP;
BEGIN
  BEGIN
BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'N') then
    goto final;
end if;

if (NEW.dt_Agenda_fim is not null) then
	delete	from agenda_controle_horario
	where	dt_agenda >= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(LOCALTIMESTAMP)
	and	dt_agenda between ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(NEW.dt_Agenda) and ESTABLISHMENT_TIMEZONE_UTILS.endOfDay(NEW.dt_Agenda_fim)
	and	cd_agenda	 = NEW.cd_agenda;
else
	delete	from agenda_controle_horario
	where	dt_agenda >= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(LOCALTIMESTAMP)
	and	dt_agenda between ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(NEW.dt_Agenda) and ESTABLISHMENT_TIMEZONE_UTILS.endOfDay(NEW.dt_Agenda)
	and	cd_agenda	 = NEW.cd_agenda;
end if;


exception
	when others then
      	dt_atualizacao := LOCALTIMESTAMP;
end;

<<final>>
null;

  END;
RETURN NEW;
END;
