SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS agenda_horario_pref_delete ON agenda_horario_pref CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_agenda_horario_pref_delete() RETURNS trigger AS $BODY$
DECLARE
dt_atualizacao 	timestamp	:= LOCALTIMESTAMP;
cd_agenda_w	bigint;
BEGIN
  BEGIN
BEGIN


select	max(cd_agenda)
into STRICT	cd_agenda_w
from 	agenda_turno
where	nr_sequencia = OLD.nr_seq_turno;

if (coalesce(cd_agenda_w,0) > 0) then
	delete	from agenda_controle_horario
	where	dt_agenda >= trunc(LOCALTIMESTAMP)
	and	cd_agenda	 = cd_agenda_w;
end if;

exception
	when others then
      	dt_atualizacao := LOCALTIMESTAMP;
end;
  END;
RETURN OLD;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_agenda_horario_pref_delete() FROM PUBLIC;

CREATE TRIGGER agenda_horario_pref_delete
	BEFORE DELETE ON agenda_horario_pref FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_agenda_horario_pref_delete();

