SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS agenda_int_item_after_atual ON agenda_integrada_item CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_agenda_int_item_after_atual() RETURNS trigger AS $BODY$
declare

cd_pessoa_fisica_w		varchar(10);
qt_medico_item_w		bigint;
qt_medico_item_inserido_w	bigint;

BEGIN

select	count(*)
into STRICT	qt_medico_item_w
from	ageint_medico_item
where	nr_seq_item	= NEW.nr_sequencia;

if (qt_medico_item_w	= 1) then
	select	max(cd_pessoa_fisica)
	into STRICT	cd_pessoa_fisica_w
	from	ageint_medico_item
	where	nr_seq_item			= NEW.nr_sequencia
	and	coalesce(ie_inserir_prof, 'N')	= 'S';

	if (cd_pessoa_fisica_w is not null) then
		select	count(*)
		into STRICT	qt_medico_item_inserido_w
		from	agenda_integrada_prof_item
		where	cd_pessoa_fisica	= cd_pessoa_fisica_w
		and	nr_seq_agenda_item	= NEW.nr_sequencia;

		if (qt_medico_item_inserido_w	= 0) then
			insert into agenda_integrada_prof_item(nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_agenda_item,
				cd_pessoa_fisica)
			values (nextval('agenda_integrada_prof_item_seq'),
				LOCALTIMESTAMP,
				NEW.nm_usuario,
				LOCALTIMESTAMP,
				NEW.nm_usuario,
				NEW.nr_sequencia,
				cd_pessoa_fisica_w);
		end if;
	end if;
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_agenda_int_item_after_atual() FROM PUBLIC;

CREATE TRIGGER agenda_int_item_after_atual
	AFTER INSERT OR UPDATE ON agenda_integrada_item FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_agenda_int_item_after_atual();

