CREATE OR REPLACE FUNCTION trigger_fct_agenda_monta_equipamento_atual() RETURNS trigger AS $BODY$
declare
ds_alteracao_w		varchar(4000);
ds_equipamento_w	varchar(255);

BEGIN

if (TG_OP = 'UPDATE') then
	if (coalesce(OLD.nr_seq_classif_equip,'-1') <> coalesce(NEW.nr_seq_classif_equip,'-1')) then
		ds_alteracao_w	:=	substr(wheb_mensagem_pck.get_texto(791491,
							'DS_CLASSIF_OLD='||obter_desc_classif_equip(OLD.nr_seq_classif_equip) ||
							';DS_CLASSIF_NEW='||obter_desc_classif_equip(NEW.nr_seq_classif_equip)),1,4000);
	end if;
	if (coalesce(OLD.ds_observacao,'X') <> coalesce(NEW.ds_observacao,'X')) then
		ds_alteracao_w	:=	substr(ds_alteracao_w || CHR(10) || CHR(13) ||
					wheb_mensagem_pck.get_texto(791471,
							'DS_OBSERVACAO_OLD='||coalesce(OLD.ds_observacao, wheb_mensagem_pck.get_texto(791500)) ||
							';DS_OBSERVACAO_NEW='||coalesce(NEW.ds_observacao, wheb_mensagem_pck.get_texto(791500))),1,4000);

	end if;
	if (ds_alteracao_w is not null) then
		CALL gravar_historico_montagem(NEW.nr_seq_agenda,'AE',ds_alteracao_w,NEW.nm_usuario);
	end if;
else
	ds_equipamento_w := substr(obter_desc_classif_equip(NEW.nr_seq_classif_equip),1,255);
	ds_alteracao_w	:=	substr(wheb_mensagem_pck.get_texto(791517,
							'DS_EQUIP='||ds_equipamento_w),1,4000);

	if (ds_alteracao_w is not null) then
		CALL gravar_historico_montagem(NEW.nr_seq_agenda,'IE',ds_alteracao_w,NEW.nm_usuario);
	end if;
end if;


RETURN NEW;
end
