CREATE OR REPLACE FUNCTION trigger_fct_agenda_monta_equip_delete() RETURNS trigger AS $BODY$
declare
ds_alteracao_w		varchar(4000);
BEGIN

if (OLD.nr_seq_classif_equip is not null) then
	ds_alteracao_w	:=	substr(obter_desc_expressao(500805)||' '||obter_desc_classif_equip(OLD.nr_seq_classif_equip),1,4000);
end if;

if (ds_alteracao_w is not null) then
	CALL gravar_historico_montagem(OLD.nr_seq_agenda,'EE',ds_alteracao_w,OLD.nm_usuario);
end if;

RETURN OLD;
end
