SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS agenda_pac_equip_atual_hl7 ON agenda_pac_equip CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_agenda_pac_equip_atual_hl7() RETURNS trigger AS $BODY$
declare
ds_sep_bv_w		varchar(100);
ds_param_integ_hl7_w	varchar(4000);
cd_pessoa_fisica_w	varchar(10);
dt_confirmacao_w	timestamp;
ie_gerar_integracao_w	varchar(1);

BEGIN

ds_sep_bv_w := obter_separador_bv;

if (TG_OP = 'INSERT')  or (OLD.cd_equipamento <> NEW.cd_equipamento)  then

	select	max(a.cd_pessoa_fisica),
		max(a.dt_confirmacao),
		max(ie_integra_rel_dixtal)
	into STRICT	cd_pessoa_fisica_w,
		dt_confirmacao_w,
		ie_gerar_integracao_w
	from	agenda b,
		agenda_paciente a
	where	a.nr_sequencia = NEW.nr_seq_agenda
	and	b.cd_agenda = a.cd_agenda;

	if (cd_pessoa_fisica_w is not null) and (dt_confirmacao_w is not null) and (ie_gerar_integracao_w = 'S') then
		ds_param_integ_hl7_w :=	'cd_pessoa_fisica=' || cd_pessoa_fisica_w || ds_sep_bv_w ||
					'nr_sequencia=' || NEW.nr_seq_agenda || ds_sep_bv_w;
		CALL gravar_agend_integracao(193, ds_param_integ_hl7_w);
	end if;
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_agenda_pac_equip_atual_hl7() FROM PUBLIC;

CREATE TRIGGER agenda_pac_equip_atual_hl7
	AFTER INSERT OR UPDATE ON agenda_pac_equip FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_agenda_pac_equip_atual_hl7();

