DROP TRIGGER IF EXISTS agenda_pac_pedido_kit_atual ON agenda_pac_pedido_kit CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_agenda_pac_pedido_kit_atual() RETURNS trigger AS $BODY$
declare
ds_alteracao_w		varchar(4000);
ds_equipamento_w	varchar(255);
nr_seq_agenda_w		agenda_pac_pedido.nr_seq_agenda%type;
BEGIN
  BEGIN

	if (wheb_usuario_pck.get_ie_executar_trigger	= 'S')  then

		BEGIN
		select	coalesce(max(nr_seq_agenda),0)
		into STRICT	nr_seq_agenda_w
		from	agenda_pac_pedido
		where	nr_sequencia = NEW.nr_seq_pedido;
		exception
		when no_data_found then
			nr_seq_agenda_w	:= 0;
		when others then
			nr_seq_agenda_w	:= 0;
		end;

		if (TG_OP = 'UPDATE') then
			if (coalesce(OLD.cd_kit_material,0) <> coalesce(NEW.cd_kit_material,0)) then
				ds_alteracao_w	:=	substr(wheb_mensagem_pck.get_texto(800123,
											'DS_KIT_OLD='||obter_descricao_padrao('KIT_MATERIAL','DS_KIT_MATERIAL',OLD.cd_kit_material)||
											';DS_KIT_NEW='||obter_descricao_padrao('KIT_MATERIAL','DS_KIT_MATERIAL',NEW.cd_kit_material)),1,4000);					
			end if;					
			if (coalesce(OLD.DS_OBSERVACAO,' ') <> coalesce(NEW.DS_OBSERVACAO,' ')) then
				ds_alteracao_w	:=	substr(wheb_mensagem_pck.get_texto(800124,
											'DS_KIT_NEW='||obter_descricao_padrao('KIT_MATERIAL','DS_KIT_MATERIAL',NEW.cd_kit_material)||
											';DS_OBSERVACAO_OLD='||OLD.DS_OBSERVACAO||
											';DS_OBSERVACAO_NEW='||NEW.DS_OBSERVACAO),1,4000);
			end if;
			if (ds_alteracao_w is not null) and (nr_seq_agenda_w > 0) then
				CALL gravar_historico_montagem(nr_seq_agenda_w,'AK',ds_alteracao_w,NEW.nm_usuario);
			end if;
		else
			ds_alteracao_w	:=	substr(wheb_mensagem_pck.get_texto(800125, 'DS_KIT_NEW='||obter_descricao_padrao('KIT_MATERIAL','DS_KIT_MATERIAL',NEW.cd_kit_material)),1,4000);
							
			if (ds_alteracao_w is not null) and (nr_seq_agenda_w > 0) then
				CALL gravar_historico_montagem(nr_seq_agenda_w,'IK',ds_alteracao_w,NEW.nm_usuario);
			end if;
		end if;	

	end if;

  END;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
