SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS agenda_quimio_notification ON agenda_quimio CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_agenda_quimio_notification() RETURNS trigger AS $BODY$
DECLARE ie_status_w varchar(1) := 'Y';



BEGIN

IF (NEW.Ie_Status_Agenda = 'N' ) THEN

BEGIN

select CASE WHEN count(*)=0 THEN  'N'  ELSE 'Y' END  into STRICT  ie_status_w from wsuite_notification WHERE nr_primary_key  = NEW.Nr_Sequencia AND ie_notification_type ='Q';

If (ie_status_w = 'N') then

CALL Wsuite_Add_Notification('Q',NEW.Nr_Sequencia, NULL, NEW.Nm_Usuario, NEW.cd_pessoa_fisica,NEW.Nr_Sequencia);

end if;

end;

Elsif (NEW.Ie_Status_Agenda  = 'C' ) THEN

BEGIN

CALL Wsuite_Remove_Notification('Q',NEW.Nr_Sequencia);

CALL Wsuite_Add_Notification('Q',NEW.Nr_Sequencia, NULL, NEW.Nm_Usuario, NEW.cd_pessoa_fisica,NEW.Nr_Sequencia);

end;

END IF;



RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_agenda_quimio_notification() FROM PUBLIC;

CREATE TRIGGER agenda_quimio_notification
	AFTER INSERT OR UPDATE ON agenda_quimio FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_agenda_quimio_notification();

