declare

ds_id_google_w	varchar(255);

pragma autonomous_transaction;
BEGIN

select	max(ds_id_google)
into STRICT	ds_id_google_w
from	agenda_google_cal_hist
where	nr_seq_agenda = OLD.nr_sequencia;

if ( ds_id_google_w is not null ) then
	insert into agenda_google_cal_del(
		nr_sequencia,
		ds_id_google,
		nm_usuario_agenda,
		nr_seq_agenda,
		dt_atualizacao,
		nm_usuario
	) values (
		nextval('agenda_google_cal_del_seq'),
		ds_id_google_w,
		OLD.nm_usuario_agenda,
		OLD.nr_sequencia,
		LOCALTIMESTAMP,
		OLD.nm_usuario_agenda
	);

	commit;
end if;

RETURN OLD;
end
$BODY$
