DROP TRIGGER IF EXISTS agenda_turno_after ON agenda_turno CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_agenda_turno_after() RETURNS trigger AS $BODY$
declare
qt_regras_w		bigint;

BEGIN

	select	count(1)
	into STRICT	qt_regras_w
	from	agenda_turno_conv
	where	nr_seq_turno = NEW.nr_sequencia;

	if (qt_regras_w > 0) then

		update 	agenda_turno_conv
		set 	ie_dia_semana = NEW.ie_dia_semana,
				hr_inicial = NEW.hr_inicial
		where 	nr_seq_turno = NEW.nr_sequencia;

	end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_agenda_turno_after() FROM PUBLIC;

CREATE TRIGGER agenda_turno_after
	AFTER INSERT OR UPDATE ON agenda_turno FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_agenda_turno_after();

