DROP TRIGGER IF EXISTS agenda_turno_classif_atual ON agenda_turno_classif CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_agenda_turno_classif_atual() RETURNS trigger AS $BODY$
DECLARE
dt_atualizacao 	timestamp	:= LOCALTIMESTAMP;
cd_agenda_w	bigint;
BEGIN
  BEGIN
BEGIN

select	max(cd_agenda)
into STRICT	cd_agenda_w
from	agenda_turno
where	nr_sequencia	= NEW.nr_seq_turno;

if (cd_agenda_w is not null) then
	delete	from agenda_controle_horario
	where	dt_agenda >= trunc(LOCALTIMESTAMP)
	and	cd_agenda	 = cd_agenda_w;
end if;

exception
	when others then
      	dt_atualizacao := LOCALTIMESTAMP;
end;
  END;
RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
