SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS agenda_turno_esp_delete ON agenda_turno_esp CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_agenda_turno_esp_delete() RETURNS trigger AS $BODY$
DECLARE
dt_atualizacao 	timestamp	:= LOCALTIMESTAMP;
ie_manter_livres_w	varchar(3);
BEGIN
  BEGIN
BEGIN

select	coalesce(max(ie_forma_excluir_consulta),'N')
into STRICT	ie_manter_livres_w
from	parametro_agenda;

if (ie_manter_livres_w	= 'N') then
	delete	from agenda_consulta
	where	dt_agenda between OLD.hr_inicial and OLD.hr_final
	and	cd_agenda = OLD.cd_agenda
	and	ie_status_agenda = 'L';
end if;

if (OLD.dt_Agenda_fim is not null) then
	delete	from agenda_controle_horario
	where	dt_agenda >= trunc(LOCALTIMESTAMP)
	and	dt_agenda between trunc(OLD.dt_Agenda) and trunc(OLD.dt_Agenda_fim) + 86399 / 86400
	and	cd_agenda	 = OLD.cd_agenda;
else
	delete	from agenda_controle_horario
	where	dt_agenda >= trunc(LOCALTIMESTAMP)
	and	dt_agenda >= trunc(OLD.dt_Agenda)
	and	cd_agenda	 = OLD.cd_agenda;
end if;
exception
	when others then
      	dt_atualizacao := LOCALTIMESTAMP;
end;
  END;
RETURN OLD;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_agenda_turno_esp_delete() FROM PUBLIC;

CREATE TRIGGER agenda_turno_esp_delete
	BEFORE DELETE ON agenda_turno_esp FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_agenda_turno_esp_delete();

