SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS alteracao_tasy_atual ON alteracao_tasy CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_alteracao_tasy_atual() RETURNS trigger AS $BODY$
DECLARE
BEGIN
	IF (TG_OP = 'INSERT') THEN
		/* ATUALIZAR O TIPO DO CLIENTE NO NEWS */

		SELECT	coalesce(MAX(IE_TIPO_CLIENTE),'P')
		INTO STRICT	NEW.IE_TIPO_CLIENTE
		FROM	FUNCAO_NET
		WHERE	CD_FUNCAO	= NEW.CD_FUNCAO;
	END IF;
	NEW.DS_UTC_ATUALIZACAO := OBTER_DATA_UTC(LOCALTIMESTAMP,'HV');
	NEW.DS_UTC := OBTER_DATA_UTC(NEW.DT_ALTERACAO,'HV');
	NEW.IE_HORARIO_VERAO := OBTER_SE_HORARIO_VERAO(NEW.DT_ALTERACAO);
RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_alteracao_tasy_atual() FROM PUBLIC;

CREATE TRIGGER alteracao_tasy_atual
	BEFORE INSERT OR UPDATE ON alteracao_tasy FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_alteracao_tasy_atual();

