SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS alteracao_valor_bef_update ON alteracao_valor CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_alteracao_valor_bef_update() RETURNS trigger AS $BODY$
declare
cd_estabelecimento_w    ctb_documento.cd_estabelecimento%type;

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then
if (coalesce(NEW.nr_titulo, 0) <> 0) then

	select	max(cd_estabelecimento)
	into STRICT	cd_estabelecimento_w
	from	titulo_receber
	where	nr_titulo = NEW.nr_titulo;
	
	/* Projeto Davita - Nao deixa gerar movimento contabil apos fechamento da data */


	CALL philips_contabil_pck.valida_se_dia_fechado(OBTER_EMPRESA_ESTAB(cd_estabelecimento_w), NEW.dt_alteracao);
	
end if;
end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_alteracao_valor_bef_update() FROM PUBLIC;

CREATE TRIGGER alteracao_valor_bef_update
	BEFORE UPDATE ON alteracao_valor FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_alteracao_valor_bef_update();

