CREATE OR REPLACE FUNCTION trigger_fct_alt_titulo_arvore_after_insert() RETURNS trigger AS $BODY$
declare

count_exec_sql_w bigint;

BEGIN

update tree_query_dar set ds_titulo = NEW.nm_query where nr_seq_filter = NEW.nr_sequencia;

select count(nr_sequencia) into STRICT count_exec_sql_w from dar_tables_control where nr_seq_sql = NEW.nr_sequencia;

if (count_exec_sql_w > 0) then
	update dar_tables_control set ds_datamodel = NEW.nm_query where nr_seq_sql = NEW.nr_sequencia;
end if;

RETURN NEW;
end
