SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS anamnese_paciente_atual ON anamnese_paciente CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_anamnese_paciente_atual() RETURNS trigger AS $BODY$
declare

qt_reg_w	smallint;
BEGIN
  BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;

if (coalesce(OLD.DT_ANANMESE,LOCALTIMESTAMP + interval '10 days') <> NEW.DT_ANANMESE) and (NEW.DT_ANANMESE is not null) then
	NEW.ds_utc				:= obter_data_utc(NEW.DT_ANANMESE,'HV');	
end if;

if (coalesce(OLD.DT_LIBERACAO,LOCALTIMESTAMP + interval '10 days') <> NEW.DT_LIBERACAO) and (NEW.DT_LIBERACAO is not null) then
	NEW.ds_utc_atualizacao	:= obter_data_utc(NEW.DT_LIBERACAO,'HV');
end if;

BEGIN
	if (NEW.nr_atendimento is not null) and (NEW.cd_pessoa_fisica is null) then
		NEW.cd_pessoa_fisica := obter_pessoa_Atendimento(NEW.nr_atendimento,'C');
	end if;

exception
	when others then
	null;
end;

<<Final>>
qt_reg_w	:= 0;

  END;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_anamnese_paciente_atual() FROM PUBLIC;

CREATE TRIGGER anamnese_paciente_atual
	BEFORE INSERT OR UPDATE ON anamnese_paciente FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_anamnese_paciente_atual();

