SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS anamnese_paciente_pend_atual ON anamnese_paciente CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_anamnese_paciente_pend_atual() RETURNS trigger AS $BODY$
declare

qt_reg_w		smallint;
ie_tipo_w		varchar(10);
ie_lib_anamnese_w	varchar(10);
nm_usuario_w        varchar(15);

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;

select	max(ie_lib_anamnese)
into STRICT	ie_lib_anamnese_w
from	parametro_medico
where	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;

if (NEW.dt_liberacao is null) then

     if (coalesce(NEW.IE_AVALIADOR_AUX,'N') = 'S') and (NEW.DT_LIBERACAO_AUX is not null)then
	
		select 	obter_usuario_pf(NEW.cd_medico)
		into STRICT	nm_usuario_w
		;
		
		Update    pep_item_pendente
		set		nm_usuario = nm_usuario_w
		where	nr_seq_anamnese = NEW.nr_sequencia;

	end if;

end if;

if (coalesce(ie_lib_anamnese_w,'N') = 'S') then
	if (NEW.dt_liberacao is null) then
		ie_tipo_w := 'AP';
	elsif (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then
		ie_tipo_w := 'XAP';
	end if;

	if (ie_tipo_w	is not null) then
    CALL Gerar_registro_pendente_PEP( cd_tipo_registro_p => ie_tipo_w,
      nr_sequencia_registro_p => NEW.nr_sequencia,
      cd_pessoa_fisica_p => coalesce(NEW.cd_pessoa_fisica,substr(obter_pessoa_atendimento(NEW.nr_atendimento,'C'),1,255)), 
      nr_atendimento_p => NEW.nr_atendimento,
      nm_usuario_p => NEW.nm_usuario,
      nr_atend_cons_pepa_p => NEW.nr_seq_atend_cons_pepa);
	end if;
end if;
	
<<Final>>
qt_reg_w	:= 0;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_anamnese_paciente_pend_atual() FROM PUBLIC;

CREATE TRIGGER anamnese_paciente_pend_atual
	AFTER INSERT OR UPDATE ON anamnese_paciente FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_anamnese_paciente_pend_atual();

