SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS apa_evolucao_atual ON apa_evolucao CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_apa_evolucao_atual() RETURNS trigger AS $BODY$
BEGIN

	IF (wheb_usuario_pck.get_ie_executar_trigger	= 'S')  THEN
		IF ( NEW.dt_evolucao IS NOT NULL ) THEN
			NEW.ds_utc := substr(obter_data_utc(NEW.dt_evolucao, 'HV'), 1, 50);
		END IF;

		IF ( ( OLD.dt_liberacao IS NULL ) OR ( OLD.dt_liberacao <> NEW.dt_liberacao ) ) AND ( NEW.dt_liberacao IS NOT NULL ) THEN
			NEW.ds_utc_atualizacao := substr(obter_data_utc(NEW.dt_liberacao, 'HV'), 1, 50);

		END IF;
	END IF;
RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_apa_evolucao_atual() FROM PUBLIC;

CREATE TRIGGER apa_evolucao_atual
	BEFORE INSERT OR UPDATE ON apa_evolucao FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_apa_evolucao_atual();

