DROP TRIGGER IF EXISTS atendimento_acomp_update ON atendimento_acompanhante CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_atendimento_acomp_update() RETURNS trigger AS $BODY$
DECLARE
ie_possui_alta_w	varchar(2);
ie_permite_alta_w     varchar(2);
BEGIN
ie_permite_alta_w := Obter_Param_Usuario(8014, 112, obter_perfil_ativo, NEW.nm_usuario, 0, ie_permite_alta_w);

Select 	coalesce(max('S'),'N')
into STRICT 	ie_possui_alta_w
from 	atendimento_paciente a
where 	NEW.nr_atendimento = a.nr_atendimento
and 	a.dt_alta is not null;

if (OLD.dt_saida is not null) and (NEW.dt_saida is null) 	and (ie_possui_alta_w = 'S') 	and (ie_permite_alta_w = 'N')		then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(284133);
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_atendimento_acomp_update() FROM PUBLIC;

CREATE TRIGGER atendimento_acomp_update
	BEFORE UPDATE ON atendimento_acompanhante FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_atendimento_acomp_update();

