DROP TRIGGER IF EXISTS atendimento_alta_atual ON atendimento_alta CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_atendimento_alta_atual() RETURNS trigger AS $BODY$
declare
qt_min_caracteres_w		bigint;
BEGIN
  BEGIN
if (coalesce(OLD.DT_DESFECHO,LOCALTIMESTAMP + interval '10 days') <> NEW.DT_DESFECHO) and (NEW.DT_DESFECHO is not null) then
	NEW.ds_utc				:= obter_data_utc(NEW.DT_DESFECHO,'HV');
end if;

if (coalesce(OLD.DT_LIBERACAO,LOCALTIMESTAMP + interval '10 days') <> NEW.DT_LIBERACAO) and (NEW.DT_LIBERACAO is not null) then
	NEW.ds_utc_atualizacao	:= obter_data_utc(NEW.DT_LIBERACAO,'HV');
end if;

if (Obter_Funcao_Ativa	= 281) then
	BEGIN
	qt_min_caracteres_w	:= Obter_Valor_Param_Usuario(281,400,Obter_Perfil_Ativo,NEW.nm_usuario,wheb_usuario_pck.get_cd_estabelecimento);
	exception
		when others then
		qt_min_caracteres_w	:= 0;
		end;

	if (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) and (qt_min_caracteres_w	> 0) and (NEW.QT_CARACTERES < qt_min_caracteres_w) then
		CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(267649);
	end if;
end if;

  END;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_atendimento_alta_atual() FROM PUBLIC;

CREATE TRIGGER atendimento_alta_atual
	BEFORE INSERT OR UPDATE ON atendimento_alta FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_atendimento_alta_atual();

