SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS atendimento_alta_beforeinsert ON atendimento_alta CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_atendimento_alta_beforeinsert() RETURNS trigger AS $BODY$
declare
cd_pessoa_fisica_w	varchar(10);
BEGIN 
 
if (NEW.ie_tipo_orientacao <> 'P') then 
	NEW.cd_setor_atendimento	:= Obter_Unidade_Atendimento(NEW.nr_atendimento,'IAA','CS');
end if;
 
 
select	max(ie_tipo_evolucao), 
	max(cd_pessoa_fisica) 
into STRICT	NEW.ie_tipo_evolucao, 
	cd_pessoa_fisica_w 
from	usuario 
where	nm_usuario	= NEW.nm_usuario;
 
if (cd_pessoa_fisica_w	is not null) then 
	NEW.cd_especialidade_medico	:= obter_especialidade_medico(cd_pessoa_fisica_w,'C');
end if;
RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_atendimento_alta_beforeinsert() FROM PUBLIC;

CREATE TRIGGER atendimento_alta_beforeinsert
	BEFORE INSERT ON atendimento_alta FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_atendimento_alta_beforeinsert();

