DROP TRIGGER IF EXISTS atendimento_boletim_pend_atual ON atendimento_boletim CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_atendimento_boletim_pend_atual() RETURNS trigger AS $BODY$
declare

qt_reg_w					smallint;
ie_tipo_w				varchar(10);
ie_lib_boletim_inf_w	varchar(15);
BEGIN
  BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;

select	max(ie_lib_boletim_inf)
into STRICT	ie_lib_boletim_inf_w
from	parametro_medico
where	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;

if (coalesce(ie_lib_boletim_inf_w,'N') = 'S') then
	if (NEW.dt_liberacao is null) then
		ie_tipo_w := 'LBI';
	elsif (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then
		ie_tipo_w := 'XLBI';
	end if;

	BEGIN
	if (ie_tipo_w	is not null) then
		CALL Gerar_registro_pendente_PEP(ie_tipo_w, NEW.nr_sequencia, obter_pessoa_atendimento(NEW.nr_atendimento,'C'), NEW.nr_atendimento, NEW.nm_usuario,'L',null,null,null,null,null,null,null,null,null,null,null,null,null,null,null);
	end if;
	exception
		when others then
		null;
	end;
end if;

<<Final>>
qt_reg_w	:= 0;
  END;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
