SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS atendimento_paciente_befinsupd ON atendimento_paciente CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_atendimento_paciente_befinsupd() RETURNS trigger AS $BODY$
declare
ie_tipo_data_w			varchar(1);	
BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'S')  then	
	if (OLD.nr_seq_pac_senha_fila is null and NEW.nr_seq_pac_senha_fila is not null and NEW.ds_senha_qmatic is null) then
		ie_tipo_data_w := obter_param_usuario(10021, 47, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_tipo_data_w);
		select	max(substr(obter_letra_verifacao_senha(coalesce(nr_seq_fila_senha_origem, nr_seq_fila_senha)) || cd_senha_gerada,1,20)),
			max(CASE WHEN ie_tipo_data_w='G' THEN dt_geracao_senha  ELSE dt_primeira_chamada END )
		into STRICT	NEW.ds_senha_qmatic,
			NEW.dt_recebimento_senha
		from	paciente_senha_fila
		where	nr_sequencia = NEW.nr_seq_pac_senha_fila;
	
	end if;
end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
