SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS atendimento_pagador_atual ON atendimento_pagador CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_atendimento_pagador_atual() RETURNS trigger AS $BODY$
DECLARE

cd_pessoa_fisica_w			varchar(10);
qt_idade_perm_w				varchar(10);
cd_estabelecimento_w		smallint;
nr_idade_w					bigint;
qt_reg_w					bigint;

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final_Exec;
end if;
if (NEW.cd_pessoa_fisica is not null)	then

	select	obter_idade_pf(NEW.cd_pessoa_fisica,LOCALTIMESTAMP,'A'),
		obter_estab_atend(NEW.nr_atendimento)
	into STRICT	nr_idade_w,
		cd_estabelecimento_w
	;

	qt_idade_perm_w := Obter_Param_Usuario(916, 149, obter_perfil_ativo, NEW.nm_usuario, cd_estabelecimento_w, qt_idade_perm_w);

	if (nr_idade_w < (qt_idade_perm_w)::numeric ) then
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(278154, 'QT_IDADE_PERM_P=' || qt_idade_perm_w);
	
	end if;
end if;

<<Final_Exec>>
qt_reg_w	:= 0;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_atendimento_pagador_atual() FROM PUBLIC;

CREATE TRIGGER atendimento_pagador_atual
	BEFORE INSERT OR UPDATE ON atendimento_pagador FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_atendimento_pagador_atual();

