SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS atendimento_sinal_vital_inup ON atendimento_sinal_vital CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_atendimento_sinal_vital_inup() RETURNS trigger AS $BODY$
DECLARE

ie_atualiza_sinal_vital_w		varchar(1);
qt_reg_w				smallint;
cd_estabelecimento_w			integer;
ie_altura_peso_pf_w			varchar(2);

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;

cd_estabelecimento_w	:= wheb_usuario_pck.get_cd_estabelecimento;

ie_atualiza_sinal_vital_w := obter_param_usuario(3130, 57, obter_perfil_ativo, NEW.nm_usuario, cd_estabelecimento_w, ie_atualiza_sinal_vital_w);

select	coalesce(max(ie_altura_peso_pf),'N')
into STRICT	ie_altura_peso_pf_w
from	parametro_atendimento
where	cd_estabelecimento	= cd_estabelecimento_w;

if (ie_atualiza_sinal_vital_w = 'S') then
	update	prescr_medica a
	set	a.qt_peso = NEW.qt_peso,
		a.qt_altura_cm = NEW.qt_altura_cm
	where	ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(a.dt_prescricao) = ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(NEW.dt_sinal_vital)
	and	a.cd_pessoa_fisica = (SELECT cd_pessoa_fisica from atendimento_paciente where nr_atendimento = NEW.nr_atendimento)
	and	exists (select 1 from paciente_atendimento b where b.nr_prescricao = a.nr_prescricao);
end if;

if (ie_altura_peso_pf_w = 'S') then
	update	pessoa_fisica
	set	qt_altura_cm	= NEW.qt_altura_cm,
		qt_peso		= NEW.qt_peso	
	where	cd_pessoa_fisica =	(SELECT	cd_pessoa_fisica
					 from	atendimento_paciente 
					 where nr_atendimento = NEW.nr_atendimento);
end if;
		


<<Final>>
qt_reg_w	:= 0;
RETURN NEW;
END;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_atendimento_sinal_vital_inup() FROM PUBLIC;

CREATE TRIGGER atendimento_sinal_vital_inup
	AFTER INSERT OR UPDATE ON atendimento_sinal_vital FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_atendimento_sinal_vital_inup();

